import React, { Component } from "react";
import Form from "./Form";
import validate from "validate.js";
import moment from "moment";
import { connect } from "react-redux";
import swal from "sweetalert";
import { CreateMilestone } from "api/projects";

validate.extend(validate.validators.datetime, {
	// The value is guaranteed not to be null or undefined but otherwise it
	// could be anything.
	parse: function(value, options) {
		return +moment.utc(value);
	},
	// Input is a unix timestamp
	format: function(value, options) {
		var format = options.dateOnly ? "YYYY-MM-DD" : "YYYY-MM-DD hh:mm:ss";
		return moment.utc(value).format(format);
	}
});

class AddMilestone extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {
				title: "",
				description: "",
				deadline: ""
			},
			isLoading: false,
			errors: {}
		};
	}
	onChangeHandler = e => {
		const { name, value } = e.target;

		this.setState(prevState => {
			return {
				data: {
					...prevState.data,
					[name]: value
				},
				errors: {
					...prevState.errors,
					[name]: []
				}
			};
		});
	};

	onSubmitHandler = () => {
		//validation
		let errors = validate(this.state.data, {
			title: { presence: { allowEmpty: false, message: "is required" } },
			description: { presence: { allowEmpty: false, message: "is required" } },
			deadline: {
				presence: { allowEmpty: false, message: "is required" },
				datetime: {
					earliest: moment.utc().add(1, "day"),
					message: "should be greater than today's date"
				}
			}
		});

		// check for errors
		if (errors) {
			this.setState({ errors });
		} else {
			this.setState({
				isLoading: true
			});
			let projectIds = this.props.selectedRows.map(row => {
				return row.id;
			});

			let data = {
				...this.state.data,
				projectIds
			};
			CreateMilestone(this.props.token, data)
				.then(data => {
					swal({
						title: "Success",
						text: "Milestone " + data.title + " has been added successfully",

						button: "Ok"
					});
					this.props.handleClose();
				})
				.catch(error => {
					swal({
						title: "Error",
						text: error.response.data.message,

						button: "Ok"
					});
					this.setState({
						isLoading: false
					});
				});
		}
	};

	render() {
		return (
			<Form
				open={this.props.open}
				handleClose={this.props.handleClose}
				onChangeHandler={this.onChangeHandler}
				onSubmit={this.onSubmitHandler}
				errors={this.state.errors}
				isLoading={this.state.isLoading}
			/>
		);
	}
}

const MapStatetoProps = state => {
	return {
		token: state.auth.token
	};
};

export default connect(MapStatetoProps)(AddMilestone);
