import React, { Component } from "react";
import ContentForm from "./Form";
import validate from "validate.js";
import { Redirect } from "react-router-dom";
import { createQuiz } from "api/quizzes";
import { connect } from "react-redux";
import { Quiz } from "serializers/quiz";
import swal from "sweetalert";

class EditEndDate extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {
				enddate: ""
			},
			isLoading: false,
			errors: {}
		};
	}

	onChangeHandler = e => {
		const { name, value } = e.target;

		this.setState(prevState => {
			return {
				data: {
					enddate: value
				},
				errors: {
					enddate: null
				}
			};
		});
	};

	onSubmitHandler = () => {
		//validation
		let errors = validate(this.state.data, {
			enddate: {
				presence: { allowEmpty: false, message: "is required" },
				datetime: {
					earliest: this.props.startdate,
					message: "Cannot be before Start date"
				}
			}
		});

		// check for errors
		if (errors) {
			this.setState({ errors });
		} else {
			this.setState({
				isLoading: true
			});
			let data = {
				expiry: this.state.data.enddate
			};
			editEndDate(this.props.token, data)
				.then(data => {
					// update data in redux
					console.log(data);
					this.props.updateEndDate(data.enddate);

					this.props.handleClose();
				})
				.catch(error => {
					swal({
						title: "Error",
						text: error.response.data.message,

						button: "Ok"
					});
					this.setState({
						isLoading: false
					});
				});
		}
	};

	render() {
		return (
			<div>
				<EditdateForm
					open={this.props.open}
					onChangeHandler={this.onChangeHandler}
					onSubmit={this.onSubmitHandler}
					errors={this.state.errors}
					data={this.state.data}
					handleClose={this.props.handleClose}
					isLoading={this.state.isLoading}
				/>
			</div>
		);
	}
}
const mapStateToProps = state => {
	return {
		token: state.auth.token
	};
};

const mapDispatchToProps = dispatch => {
	return {
		updateEndDate: (enddate, quizid) =>
			dispatch({
				type: "UPDATE END DATE",
				updatedDate: enddate,
				quizid: quizid
			})
	};
};

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(CreateQuiz);
