import React, { Component } from "react";

import { Dashboard as DashboardLayout } from "layouts";

import StudymaterialToolbar from "./components/StudymaterialToolbar";
import MaterialTable from "material-table";

// styles
import styles from "./styles";
import { withStyles } from "@material-ui/styles";

class StudymaterialList extends Component {
	columns = [
		{
			name: "title",
			label: "Title",
			options: {
				filter: true,
				sort: true
			}
		},
		{
			name: "skills",
			label: "Skills",
			options: {
				filter: true,
				sort: false
			}
		},
		{
			name: "team",
			label: "Team",
			options: {
				filter: true,
				sort: false
			}
		}
	];

	data = [
		{ id: "dsfsd", title: "Joe James", skills: "Test Corp", team: "Yonkers" },
		{
			id: "desd",
			title: "John Walsh",
			skills: "Test Corp",
			team: "Hartford"
		},
		{ id: "wfsd", title: "Bob Herm", skills: "Test Corp", team: "Tampa" },
		{
			id: "dsxcsfsd",
			title: "James Houston",
			skills: "Test Corp",
			team: "Dallas"
		}
	];

	options = {
		filtering: true,
		search: true
	};

	render() {
		const { classes } = this.props;

		return (
			<DashboardLayout title="Studymaterial">
				<div className={classes.root}>
					<StudymaterialToolbar />
					<MaterialTable
						className={classes.content}
						title="Study Material List"
						data={this.data}
						columns={this.columns}
						options={this.options}
					/>
				</div>
			</DashboardLayout>
		);
	}
}

export default withStyles(styles)(StudymaterialList);
