import React, { Component } from "react";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";

import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import Select from "@material-ui/core/Select";
import moment from "moment";

import styles from "./styles";
import { withStyles } from "@material-ui/styles";
import validate from "validate.js";
import { sendScheduledata } from "api/quizzes";
import { connect } from "react-redux";
import { loadStudentGroups, loadLectures } from "api/quizzes";
import { StudentGroup, Lecture } from "serializers/quiz";

validate.extend(validate.validators.datetime, {
	// The value is guaranteed not to be null or undefined but otherwise it
	// could be anything.
	parse: function(value, options) {
		return +moment.utc(value);
	},
	// Input is a unix timestamp
	format: function(value, options) {
		var format = options.dateOnly ? "YYYY-MM-DD" : "YYYY-MM-DD hh:mm:ss";
		return moment.utc(value).format(format);
	}
});

class schedulequiz extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {
				startdate: "",
				enddate: "",
				timelimit: "",
				receipents: []
			},
			receipents: [],
			errors: {}
		};
	}

	componentDidMount() {
		this.loadreceipents();
	}

	loadreceipents = () => {
		loadStudentGroups(this.props.token)
			.then(data => {
				let studentgroups = data.groups.map(group => {
					return new StudentGroup(group);
				});

				this.setState(prevState => {
					return {
						receipents: [...prevState.receipents, ...studentgroups]
					};
				});
			})
			.catch();

		loadLectures(this.props.token)
			.then(data => {
				console.log(data.lectures);
				let lectures = data.lectures.map(lecture => {
					return new Lecture(lecture);
				});

				this.setState(prevState => {
					return {
						receipents: [...prevState.receipents, ...lectures]
					};
				});
			})
			.catch();
	};

	startdateHandler = e => {
		let value = e.target.value;
		this.setState(prevState => {
			return {
				data: { ...prevState.data, startdate: value },
				errors: { ...prevState.errors, startdate: "" }
			};
		});
	};

	enddateHandler = e => {
		let value = e.target.value;
		this.setState(prevState => {
			return {
				data: { ...prevState.data, enddate: value },
				errors: { ...prevState.errors, enddate: "" }
			};
		});
	};

	timelimitHandler = e => {
		let value = e.target.value;
		this.setState(prevState => {
			return {
				data: { ...prevState.data, timelimit: value },
				errors: { ...prevState.errors, timelimit: "" }
			};
		});
	};

	onSelectChangeHandler = e => {
		let receipents = [...e.target.value];

		console.log(receipents);
		this.setState(prevState => {
			return {
				data: {
					...prevState.data,
					receipents: receipents
				},
				errors: {
					...prevState.errors,
					receipents: null
				}
			};
		});
	};

	schedulequizHandler = () => {
		// validation of user input
		let errors = validate(this.state.data, {
			startdate: {
				presence: { allowEmpty: false, message: "is required" },
				datetime: {
					dateOnly: true,
					earliest: moment.utc(),
					message: "Cannot be before Today's date"
				}
			},
			enddate: {
				presence: { allowEmpty: false, message: "is required" },
				datetime: {
					dateOnly: true,
					earliest: this.state.data.startdate,
					message: "Cannot be before Start date"
				}
			},
			timelimit: {
				presence: { allowEmpty: false, message: "is required" }
			},
			receipents: {
				presence: { allowEmpty: false, message: "are required" }
			}
		});

		if (errors) {
			this.setState({
				errors
			});
		} else {
			console.log(this.state.data);
			this.sendScheduledata();
		}
	};

	sendScheduledata = () => {
		sendScheduledata(this.props.token)
			.then()
			.catch();
	};

	render() {
		return (
			<div className={this.props.classes.root}>
				<Typography style={{ textAlign: "center" }} variant="h3">
					{this.props.quiztitle}
				</Typography>
				<Typography style={{ textAlign: "center" }} variant="subtitle1">
					Scheduling Status: {this.props.schedulingStatus}
				</Typography>
				<Grid container spacing={2} className={this.props.classes.content}>
					<Grid item md={3}>
						<Typography>Start Date</Typography>
						<TextField
							type="date"
							onChange={this.startdateHandler}
							value={this.state.data.startdate}
						/>
						<br />
						<Typography variant="caption">Day when quiz begins</Typography>
						<br />
						<Typography variant="caption" color="error">
							{this.state.errors.startdate && this.state.errors.startdate[0]}
						</Typography>
					</Grid>
					<Grid item md={3}>
						<Typography>End Date</Typography>
						<TextField
							type="date"
							onChange={this.enddateHandler}
							value={this.state.data.enddate}
						/>
						<br />
						<Typography variant="caption">Last day to give quiz</Typography>
						<br />
						<Typography variant="caption" color="error">
							{this.state.errors.enddate && this.state.errors.enddate[0]}
						</Typography>
					</Grid>
					<Grid item md={3}>
						<Typography>Time limit</Typography>

						<TextField
							label="Number"
							type="number"
							onChange={this.timelimitHandler}
							value={this.state.data.timelimit}
						/>
						<br />
						<Typography variant="caption">(in seconds)</Typography>
						<br />
						<Typography variant="caption" color="error">
							{this.state.errors.timelimit && this.state.errors.timelimit[0]}
						</Typography>
					</Grid>
					<Grid item md={3}>
						<Typography>Participants of Quiz</Typography>
						<Select
							multiple
							value={this.state.data.receipents}
							name="receipents"
							error={this.state.errors.receipents}
							onChange={this.onSelectChangeHandler}
							input={<OutlinedInput name="receipents" labelWidth={120} />}
							renderValue={selected => "Sending to " + selected.length}>
							{this.state.receipents.map(receipent => {
								return (
									<MenuItem
										key={receipent.id}
										value={JSON.stringify(receipent)}>
										{receipent.name}
									</MenuItem>
								);
							})}
						</Select>
						<br />
						<Typography variant="caption" color="error">
							{this.state.errors.receipents && this.state.errors.receipents[0]}
						</Typography>
					</Grid>
				</Grid>

				<Button
					variant="contained"
					color="primary"
					onClick={this.schedulequizHandler}>
					Schedule Quiz
				</Button>
			</div>
		);
	}
}

const mapStateToProps = (state, props) => {
	let title = "";
	state.quiz.quizlistTabledata.map(quiz => {
		if (quiz.id == props.match.params.quizId) {
			title = quiz.title;
		}
	});

	let schedulingStatus = "";
	state.quiz.quizlistTabledata.map(quiz => {
		if (quiz.id == props.match.params.quizId) {
			switch (quiz.schedulingStatus) {
				case 0:
					schedulingStatus = "Not Scheduled";
					break;
				case 1:
					schedulingStatus = "Scheduled";
					break;

				case 2:
					schedulingStatus = "Disabled";
					break;

				case 3:
					schedulingStatus = "Expired";
					break;
				default:
					schedulingStatus = "Default case";
			}
		}
	});

	return {
		token: state.auth.token,
		quiztitle: title,
		schedulingStatus: schedulingStatus
	};
};

export default connect(mapStateToProps)(withStyles(styles)(schedulequiz));
