import moment from "moment";

export class Project {
	constructor(project) {
		this.id = project.id;
		this.title = project.title;
		this.skills = project.tags.map(tag => {
			return {
				id: tag.id,
				skill: tag.tag
			};
		});
		this.team = project.users.map(user => {
			return {
				name: user.firstName + " " + user.lastName,
				id: user.id
			};
		});
	}
}

export class Message {
	constructor(message) {
		this.text = message.text;
		this.createdAt = moment().format(message.createdAt);
		this.sender = this.resolveSender(
			message.creator,
			message.faculty,
			message.user
		);
	}

	resolveSender = (creator, faculty, user) => {
		switch (creator) {
			case "user":
				return user.firstName + " " + user.lastName;
				break;
			case "faculty":
				return faculty.title + faculty.firstName + " " + faculty.lastName;
		}
	};
}
