import React from "react";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";

import Button from "@material-ui/core/Button";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import Dialog from "@material-ui/core/Dialog";
import DialogTitle from "@material-ui/core/DialogTitle";

import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import OutlinedInput from "@material-ui/core/OutlinedInput";

import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import InputAdornment from "@material-ui/core/InputAdornment";

import DeleteIcon from "@material-ui/icons/Delete";
import { makeStyles } from "@material-ui/styles";

const useStyles = makeStyles(theme => ({
	closeButton: {
		position: "absolute",
		right: theme.spacing(1),
		top: theme.spacing(1),
		color: theme.palette.grey[500]
	},
	splitFormSpacing: {
		marginTop: "16px",
		marginBottom: "8px"
	}
}));

const CreateQuiz = props => {
	const classes = useStyles();

	return (
		<Dialog
			onClose={props.handleClose}
			open={props.open}
			fullWidth
			maxWidth="md"
			disableBackdropClick={true}>
			<DialogTitle>
				<Typography variant="h3">Create Quiz</Typography>

				<IconButton className={classes.closeButton} onClick={props.handleClose}>
					<CloseIcon />
				</IconButton>
			</DialogTitle>
			<DialogContent dividers>
				<Grid className={classes.splitFormSpacing} item xs={12}>
					<TextField
						value={props.data.title}
						label="Quiz Title"
						variant="outlined"
						fullWidth
						name="title"
						onChange={props.onChangeHandler}
						required={true}
						error={props.errors.title}
					/>
					<Typography color="error" variant="caption">
						{props.errors.title && props.errors.title[0]}
					</Typography>
				</Grid>

				<Grid className={classes.splitFormSpacing} item xs={12}>
					{" "}
					<TextField
						name="text"
						variant="outlined"
						label="Add a Skill"
						value={props.data.text}
						onChange={props.onChangeHandler}
						InputProps={{
							endAdornment: (
								<InputAdornment position="end">
									<Fab
										size="small"
										color="primary"
										aria-label="Add"
										onClick={props.onAdd}>
										<AddIcon />
									</Fab>
								</InputAdornment>
							)
						}}
					/>
					<Typography color="error" variant="caption">
						{props.data.warning}
					</Typography>
				</Grid>
				<Skillsadded
					skills={props.data.skills}
					deleteHandler={props.onDelete}
				/>
			</DialogContent>
			<DialogActions>
				<Button variant="contained" color="primary" onClick={props.onSubmit}>
					Create Quiz
				</Button>
			</DialogActions>
		</Dialog>
	);
};

class Skillsadded extends React.Component {
	render() {
		return (
			<ul>
				{this.props.skills.map(skill => (
					<li key={skill.id}>
						{skill.text}{" "}
						<IconButton
							aria-label="Delete"
							onClick={() => this.props.deleteHandler(skill.id)}>
							<DeleteIcon />
						</IconButton>
					</li>
				))}
			</ul>
		);
	}
}

export default CreateQuiz;
