export class Quiz {
	constructor(quiz) {
		this.id = quiz.id;
		this.title = quiz.title;
		this.skills = quiz.id;
		this.numberOfQuestions = Number(quiz.noOfQuestions);
		this.schedulingStatus = getSchedulindStatus(quiz);

		this.sechedule = quiz.id;
	}

	getSchedulindStatus(quiz) {
		let a;
		if (quiz.isActive && quiz.schedule) {
			a = 1;
			return a;
		} else if (!quiz.isActive && quiz.schedule) {
			a = 2;
			return a;
		} else if (!quiz.expiry) {
			a = 0;
			return a;
		} else if (quiz.isActive && quiz.schedule && quiz.expiry) {
			a = 3;
			return a;
		}
	}
}
