import React, { Component } from "react";

// Externals
import classNames from "classnames";
import PropTypes from "prop-types";

// Material helpers
import { withStyles } from "@material-ui/styles";

// Material components
import { Button, TextField } from "@material-ui/core";

// Shared components
import {
	Portlet,
	PortletHeader,
	PortletLabel,
	PortletContent,
	PortletFooter
} from "components";

// Component styles
import styles from "./styles";

class Account extends Component {
	handleChange = e => {
		this.setState({
			state: e.target.value
		});
	};

	render() {
		const { classes, className, ...rest } = this.props;
		const {
			firstName,
			lastName,
			email,
			title,
			designation,
			mobileNumber
		} = this.props.me;

		const rootClassName = classNames(classes.root, className);

		return (
			<Portlet {...rest} className={rootClassName}>
				<PortletHeader>
					<PortletLabel
						subtitle="The information can be edited"
						title="Profile"
					/>
				</PortletHeader>
				<PortletContent noPadding>
					<form autoComplete="off" noValidate>
						<div className={classes.field}>
							<TextField
								className={classes.textField}
								helperText="Please specify the Title"
								label="Title"
								margin="dense"
								required
								value={title}
								variant="outlined"
							/>
							<TextField
								className={classes.textField}
								helperText="Please specify the first name"
								label="First name"
								margin="dense"
								required
								value={firstName}
								variant="outlined"
							/>
							<TextField
								className={classes.textField}
								label="Last name"
								margin="dense"
								required
								value={lastName}
								variant="outlined"
							/>
						</div>
						<div className={classes.field}>
							<TextField
								className={classes.textField}
								label="Designation"
								margin="dense"
								required
								value={designation}
								variant="outlined"
							/>
							<TextField
								className={classes.textField}
								label="Email Address"
								margin="dense"
								required
								value={email}
								variant="outlined"
							/>
							<TextField
								className={classes.textField}
								label="Phone Number"
								margin="dense"
								type="number"
								value={mobileNumber}
								variant="outlined"
							/>
						</div>
						{/* <div className={classes.field}>
              <TextField
                className={classes.textField}
                label="Select State"
                margin="dense"
                onChange={this.handleChange}
                required
                select
                SelectProps={{ native: true }}
                value={state}
                variant="outlined">
                {states.map(option => (
                  <option
                    key={option.value}
                    value={option.value}
                  >
                    {option.label}
                  </option>
                ))}
              </TextField>
              <TextField
                className={classes.textField}
                label="Country"
                margin="dense"
                required
                value={country}
                variant="outlined"
              />
                </div> */}
					</form>
				</PortletContent>
				<PortletFooter className={classes.portletFooter}>
					<Button color="primary" variant="contained">
						Save details
					</Button>
				</PortletFooter>
			</Portlet>
		);
	}
}

Account.propTypes = {
	className: PropTypes.string,
	classes: PropTypes.object.isRequired
};

export default withStyles(styles)(Account);
