import React, { Component } from "react";

import { Dashboard as DashboardLayout } from "layouts";

import ProjectToolbar from "./components/ProjectToolbar";
import MaterialTable from "material-table";
import IconButton from "@material-ui/core/IconButton";
import ViewIcon from "@material-ui/icons/Visibility";
import { Loader } from "components";
import { connect } from "react-redux";
// styles
import styles from "./styles";
import { withStyles } from "@material-ui/styles";
import { loadProjectList } from "api/projects";
import { Project } from "serializers/projects";
import CreateEvent from "./components/CreateEvent";
import Milestone from "./components/Milestone";
import Projectgroup from "./components/Projectgroup";
class ProjectList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {},
			isLoading: true,
			open: null
		};
	}

	componentDidMount() {
		this.loadData();
	}

	loadData = () => {
		loadProjectList(this.props.token)
			.then(data => {
				console.log(data.projects);
				let projects = data.projects.map(project => {
					return new Project(project);
				});

				this.setState(prevState => {
					return {
						data: {
							projects
						},
						isLoading: false
					};
				});
			})
			.catch(error => {
				console.log(error);
			});
	};

	columns = [
		{
			field: "id",
			title: "id",
			hidden: true,
			options: {
				filter: true,
				sort: true
			}
		},
		{
			field: "title",
			title: "Title",
			options: {
				filter: true,
				sort: true
			}
		},
		{
			field: "skills",
			title: "Skills",
			options: {
				filter: true,
				sort: false
			},
			render: rowData =>
				rowData.skills.map(skill => {
					return <li>{skill.skill}</li>;
				})
		},
		{
			field: "team",
			title: "Team",
			options: {
				filter: true,
				sort: false
			},
			render: rowData =>
				rowData.team.map(student => {
					return <li>{student.name}</li>;
				})
		},
		{
			title: "View",
			render: rowData => (
				<IconButton onClick={() => this.handleViewProject(rowData.id)}>
					<ViewIcon />
				</IconButton>
			)
		}
	];

	handleViewProject = id => {
		this.props.history.push("/project/" + id);
	};

	data = [
		{ id: "dsfsd", title: "Joe James", skills: "Test Corp", team: "Yonkers" },
		{
			id: "desd",
			title: "John Walsh",
			skills: "Test Corp",
			team: "Hartford"
		},
		{ id: "wfsd", title: "Bob Herm", skills: "Test Corp", team: "Tampa" },
		{
			id: "dsxcsfsd",
			title: "James Houston",
			skills: "Test Corp",
			team: "Dallas"
		}
	];

	handleSelection = rows => {
		console.log(rows);
	};

	handleClickOpen = formName => {
		switch (formName) {
			case "Event":
				this.setState({
					open: fromName
				});
				break;
			case "Group":
				// code block
				break;
			case "Group":
				// code block
				break;
			case "Milestone":
			// code block
		}
		this.setState({
			open: fromName
		});
	};
	handleClose = () => {
		this.setState({
			open: null
		});
	};

	render() {
		const { classes } = this.props;

		return (
			<DashboardLayout title="Projects">
				<div className={classes.root}>
					<ProjectToolbar handleClickOpen={this.handleClickOpen} />
					{this.state.open === "Event" && (
						<CreateEvent
							open={this.state.open === "Event"}
							handleClose={this.handleClose}
						/>
					)}
					{this.state.open === "Group" && (
						<Projectgroup
							open={this.state.open === "Group"}
							handleClose={this.handleClose}
						/>
					)}
					{this.state.open === "Milestone" && (
						<Milestone
							open={this.state.open === "Milestone"}
							handleClose={this.handleClose}
						/>
					)}

					<div className={classes.content}>
						{!this.state.isLoading ? (
							<MaterialTable
								title={"Project List"}
								data={this.state.data.projects}
								columns={this.columns}
								onSelectionChange={this.handleSelection}
								options={{
									filtering: true,
									selection: true
								}}
							/>
						) : (
							<Loader />
						)}
					</div>
				</div>
			</DashboardLayout>
		);
	}
}

const MapStatetoProps = state => {
	return {
		token: state.auth.token
	};
};

export default connect(MapStatetoProps)(withStyles(styles)(ProjectList));
