import React, { Component } from "react";

// Externals
import { Doughnut } from "react-chartjs-2";
import classNames from "classnames";
import PropTypes from "prop-types";

// Material helpers
import { withStyles } from "@material-ui/styles";

// Material components
import { IconButton, Typography } from "@material-ui/core";

// Material icons
import {
	LaptopMac as LaptopMacIcon,
	PhoneIphone as PhoneIphoneIcon,
	Refresh as RefreshIcon,
	TabletMac as TabletMacIcon
} from "@material-ui/icons";

// Shared components
import {
	Portlet,
	PortletHeader,
	PortletLabel,
	PortletToolbar,
	PortletContent
} from "components";

// Palette
import palette from "theme/palette";

// Chart configuration

// Component styles
import styles from "./styles";

class StudentCategorization extends Component {
	// Chart options
	options = {
		legend: {
			display: false
		},
		responsive: true,
		maintainAspectRatio: false,
		animation: false,
		cutoutPercentage: 80,
		layout: { padding: 0 },
		tooltips: {
			enabled: true,
			mode: "index",
			intersect: false,
			borderWidth: 1,
			borderColor: palette.border,
			backgroundColor: palette.common.white,
			titleFontColor: palette.text.primary,
			bodyFontColor: palette.text.secondary,
			footerFontColor: palette.text.secondary
		}
	};
	render() {
		let data = {
			datasets: [
				{
					data: [63, 15, 22],
					backgroundColor: [
						palette.primary.main,
						palette.danger.main,
						palette.warning.main
					],
					borderWidth: 8,
					borderColor: palette.common.white,
					hoverBorderColor: palette.common.white
				}
			],
			labels: ["Desktop", "Tablet", "Mobile"]
		};

		// Chart options
		let options = {
			legend: {
				display: false
			},
			responsive: true,
			maintainAspectRatio: false,
			animation: false,
			cutoutPercentage: 80,
			layout: { padding: 0 },
			tooltips: {
				enabled: true,
				mode: "index",
				intersect: false,
				borderWidth: 1,
				borderColor: palette.border,
				backgroundColor: palette.common.white,
				titleFontColor: palette.text.primary,
				bodyFontColor: palette.text.secondary,
				footerFontColor: palette.text.secondary
			}
		};

		const { classes, className, ...rest } = this.props;

		const rootClassName = classNames(classes.root, className);

		return (
			<Portlet {...rest} className={rootClassName}>
				<PortletHeader noDivider>
					<PortletLabel title="Student Categorization" />
				</PortletHeader>
				<PortletContent>
					<div className={classes.chartWrapper}>
						<Doughnut data={data} options={options} />
					</div>
					<div className={classes.stats}>
						<div className={classes.device}>
							<LaptopMacIcon className={classes.deviceIcon} />
							<Typography variant="body1">Desktop</Typography>
							<Typography style={{ color: palette.primary.main }} variant="h2">
								63%
							</Typography>
						</div>
						<div className={classes.device}>
							<TabletMacIcon className={classes.deviceIcon} />
							<Typography variant="body1">Tablet</Typography>
							<Typography style={{ color: palette.danger.main }} variant="h2">
								15%
							</Typography>
						</div>
						<div className={classes.device}>
							<PhoneIphoneIcon className={classes.deviceIcon} />
							<Typography variant="body1">Mobile</Typography>
							<Typography style={{ color: palette.warning.main }} variant="h2">
								23%
							</Typography>
						</div>
					</div>
				</PortletContent>
			</Portlet>
		);
	}
}

StudentCategorization.propTypes = {
	className: PropTypes.string,
	classes: PropTypes.object.isRequired
};

export default withStyles(styles)(StudentCategorization);
