import React, { Component } from "react";
import Dialog from "@material-ui/core/Dialog";
import MuiDialogTitle from "@material-ui/core/DialogTitle";
// Externals
import PropTypes from "prop-types";
import classNames from "classnames";

// Material helpers
import { withStyles } from "@material-ui/styles";

// Material components
import { Button, IconButton, Grid } from "@material-ui/core";

// Material icons
import {
	ArrowDownward as ArrowDownwardIcon,
	ArrowUpward as ArrowUpwardIcon,
	Delete as DeleteIcon
} from "@material-ui/icons";

// Component styles
import styles from "./styles";

class StudymaterialToolbar extends Component {
	constructor(props) {
		super(props);
		this.state = {
			open: false
		};
	}

	handleClickOpen = () => {
		this.setState({
			open: true
		});
	};
	handleClose = () => {
		this.setState({
			open: false
		});
	};

	render() {
		const { classes, className, selectedUsers } = this.props;

		const rootClassName = classNames(classes.root, className);

		return (
			<div className={rootClassName}>
				<Grid container justify="flex-end">
					<Button
						className={classes.btnToolbar}
						size="small"
						variant="outlined"
						onClick={this.handleClickOpen}>
						Send Content
					</Button>
					<Dialog onClose={this.handleClose} open={this.state.open} />
				</Grid>
			</div>
		);
	}
}

StudymaterialToolbar.propTypes = {
	className: PropTypes.string,
	classes: PropTypes.object.isRequired,
	selectedUsers: PropTypes.array
};

StudymaterialToolbar.defaultProps = {
	selectedUsers: []
};

export default withStyles(styles)(StudymaterialToolbar);
