import React, { Component } from "react";

import { Dashboard as DashboardLayout } from "layouts";

import StudymaterialToolbar from "./components/StudymaterialToolbar";
import SendContent from "./components/SendContent";
import Loader from "./components/loader";
import MaterialTable from "material-table";
import { fetchContents } from "api/contents";
import { connect } from "react-redux";
import { Content } from "serializers/content";
import swal from "sweetalert";
import { Typography } from "@material-ui/core";

// styles
import styles from "./styles";
import { withStyles } from "@material-ui/styles";

class StudymaterialList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			open: false,
			openMode: "Send",
			editData: null,
			contentTableData: [],
			isLoading: true
		};
	}

	componentDidMount() {
		this.loadData();
	}
	loadData = () => {
		fetchContents(this.props.token)
			.then(response => {
				let contentTableData = response.tasks.map(task => {
					return new Content(task);
				});

				this.setState({
					contentTableData: contentTableData,
					isLoading: false
				});
			})
			.catch(error => {
				swal({
					title: "Internal Server Error",
					text: "Please Try Refreshing Page",

					button: "Ok"
				});
			});
	};

	handleClickOpen = (openMode = "Send", editData = null) => {
		this.setState({
			open: true,
			openMode,
			editData
		});
	};
	handleClose = () => {
		this.setState({
			open: false,
			openMode: "Send",
			editData: null
		});
	};

	AddContentTableData = newrow => {
		let contentTableData = [...this.state.contentTableData];
		contentTableData.push(new Content(newrow));
		this.setState({
			contentTableData
		});
	};

	updateContentTableData = updatedRow => {
		let contentTableData = [...this.state.contentTableData];
		contentTableData = contentTableData.map(row => {
			if (row.id == updatedRow.id) {
				let editedRow = {
					...updatedRow,
					groups: [...row.groups],
					lectures: [...row.lectures]
				};
				return editedRow;
			}
			return row;
		});
		this.setState({
			contentTableData
		});
	};
	columns = [
		{
			title: "Title",
			field: "title",
			cellStyle: {
				"font-size": "15px"
			}
		},
		{
			title: "Description",
			field: "description",
			cellStyle: {
				"font-size": "15px"
			}
		},
		{
			title: "Type",
			field: "type",
			cellStyle: {
				"font-size": "15px"
			}
		},
		{
			title: "Groups",
			field: "groups",
			cellStyle: {
				"font-size": "15px"
			},
			render: rowData =>
				rowData.groups.map(group => {
					return <li>{group}</li>;
				})
		},
		{
			title: "Lectures",
			field: "lectures",
			cellStyle: {
				"font-size": "15px"
			},
			render: rowData => {
				if (!(rowData.lectures.length === 0)) {
					return rowData.lectures.map(lecture => {
						return <li>{lecture}</li>;
					});
				} else {
					return <p style={{ color: "#d7d7d7" }}>No lectures added</p>;
				}
			}
		},
		{
			title: "Url",
			field: "url",
			hidden: true
		},
		{
			title: "FileUrl",
			field: "fileurl",
			hidden: true
		},
		{
			title: "id",
			field: "id",
			hidden: true
		}
	];

	actions = [
		{
			icon: "visibility",
			tooltip: "View Content url",
			onClick: (event, rowData) => {
				var win = window.open(rowData.url, "_blank");
				win.focus();
			}
		},
		{
			icon: "cloud_download",
			tooltip: "Download Content file",
			onClick: (event, rowData) => {
				var win = window.open(rowData.fileurl, "_blank");
				win.focus();
			}
		},
		{
			icon: "edit",
			tooltip: "Edit Content",
			onClick: (event, rowData) => {
				return this.handleClickOpen("Edit", rowData);
			}
		}
	];

	render() {
		const { classes } = this.props;

		return (
			<DashboardLayout title="Studymaterial">
				<div className={classes.root}>
					<StudymaterialToolbar handleClickOpen={this.handleClickOpen} />
					{this.state.open && (
						<SendContent
							open={this.state.open}
							handleClose={this.handleClose}
							openMode={this.state.openMode}
							data={this.state.editData}
							AddContentTableData={this.AddContentTableData}
							updateContentTableData={this.updateContentTableData}
						/>
					)}
					<div className={classes.content}>
						{!this.state.isLoading ? (
							<MaterialTable
								columns={this.columns}
								data={this.state.contentTableData}
								options={{
									filtering: true,
									actionsColumnIndex: -1,
									headerStyle: {
										"font-size": "18px"
									}
								}}
								actions={this.actions}
								title={
									<Typography
										style={{ "font-size": "18px", fontWeight: "bolder" }}>
										Study Material List
									</Typography>
								}
							/>
						) : (
							<Loader />
						)}
					</div>
				</div>
			</DashboardLayout>
		);
	}
}

const mapStateToProps = state => {
	return {
		token: state.auth.token
	};
};

export default connect(mapStateToProps)(withStyles(styles)(StudymaterialList));
