import React, { Component } from "react";
import ContentForm from "./Form";
import validate from "validate.js";

class QuestionsForm extends Component {
	constructor(props) {
		super(props);

		this.state = this.getInitialState(props);
	}

	static defaultProps = {
		mode: "ADD"
	};

	getInitialState = props => {
		if (props.mode === "EDIT") {
			return {
				data: {
					title: "",
					description: "",
					url: "",
					type: "",
					year: "",
					file: "",
					...props.data
				},
				errors: {}
			};
		} else {
			// default mode is assumed to be ADD
			return {
				data: {
					questiontitle: "",
					options: ["", ""],
					correctOption: "",
					difficultyLevel: "",
					skills: []
				},
				errors: {}
			};
		}
	};

	onChangeHandler = e => {
		const { name, value } = e.target;

		this.setState(prevState => {
			return {
				data: {
					...prevState.data,
					[name]: value
				},
				errors: {
					...prevState.errors,
					[name]: null
				}
			};
		});
	};

	onaddOption = () => {
		this.setState(prevState => {
			return {
				data: {
					...prevState.data,
					options: [...prevState.data.options, ""]
				}
			};
		});
	};
	ondeleteOption = (e, index) => {
		if (this.state.data.options.length > 2) {
			let options = this.state.data.options;
			options = options.splice(index, 1);
			this.setState(prevState => {
				return {
					data: {
						...prevState.data,

						options: [...options]
					}
				};
			});
		}
	};
	onOptionChange = (e, index) => {
		console.log("hey");
		let options = this.state.data.options;
		options[index] = e.target.option;

		if (this.state.errors.options) {
			let optionsErr = this.state.errors.options;
			optionsErr[index] = null;
			this.setState(prevState => {
				return {
					errors: {
						...prevState.errors,
						options: [...optionsErr]
					}
				};
			});
		}
		this.setState(prevState => {
			return {
				data: {
					...prevState.data,
					options: [...options]
				}
			};
		});
	};

	skillChange = (e, checked, skill) => {
		this.setState(prevState => {
			return {
				errors: {
					...prevState.errors,
					skills: null
				}
			};
		});

		if (checked) {
			this.setState(prevState => {
				return {
					data: {
						...prevState.data,
						skills: [...prevState.data.skills, skill]
					}
				};
			});
		} else {
			let skills = this.state.data.skills;
			let index = skills.indexOf(skill);
			skills.splice(index, 1);
			this.setState(prevState => {
				return {
					data: {
						...prevState.data,
						skills: [...skills]
					}
				};
			});
		}
	};

	OnCorrectOptionChange = (e, checked, index) => {
		this.setState(prevState => {
			return {
				errors: {
					...prevState.errors,
					correctOption: null
				}
			};
		});
		let correctoption = this.state.data.options[index];
		if (checked) {
			this.setState(prevState => {
				return {
					data: { ...prevState.data, correctOption: correctoption }
				};
			});
		} else {
			this.setState(prevState => {
				return {
					data: { ...prevState.data, correctOption: "" }
				};
			});
		}
	};

	onSubmitHandler = () => {
		console.log("submission handler");
		//validation
		let errors = validate(this.state.data, {
			questiontitle: {
				presence: { allowEmpty: false, message: "is required" }
			},
			difficultyLevel: {
				presence: { allowEmpty: false, message: "is required" }
			},
			skills: {
				presence: { allowEmpty: false, message: "is required" }
			},
			correctOption: {
				presence: { allowEmpty: false, message: "is required" }
			}
		});

		let optionsErr = this.state.data.options.map(option => {
			let error = {};

			if (option === "") {
				error.Option = "*required";
			}

			return Object.keys(error).length > 0 ? error : null;
		});

		// check for errors
		if (optionsErr.some(val => val !== null) && errors) {
			this.setState(prevState => {
				return {
					data: {
						...prevState.data
					},

					errors: {
						...prevState.errors,
						...errors,
						options: optionsErr
					}
				};
			});
		} else if (errors) {
			this.setState({ errors });
		} else {
			this.setState(prevState => {
				return {
					data: {
						...prevState.data
					},

					errors: {
						...prevState.errors,

						options: optionsErr
					}
				};
			});
		}

		console.log(errors);
	};

	render() {
		return (
			<ContentForm
				open={this.props.open}
				onChangeHandler={this.onChangeHandler}
				onSubmit={this.onSubmitHandler}
				errors={this.state.errors}
				data={this.state.data}
				years={this.years}
				types={this.types}
				handleCloseForm={this.props.handleCloseForm}
				mode={this.props.mode}
				skills={this.props.skills}
				onaddOption={this.onaddOption}
				ondeleteOption={this.ondeleteOption}
				onSkillChange={this.skillChange}
				skillChange={this.skillChange}
				onOptionChange={this.onOptionChange}
				OnCorrectOptionChange={this.OnCorrectOptionChange}
			/>
		);
	}
}

export default QuestionsForm;
