import React, { Component } from "react";

import { Dashboard as DashboardLayout } from "layouts";

import QuizToolbar from "./components/QuizToolbar";
import SendContent from "./components/SendContent";
import MaterialTable from "material-table";

// styles
import styles from "./styles";
import { withStyles } from "@material-ui/styles";

class QuizList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			open: false
		};
	}

	handleClickOpen = () => {
		this.setState({
			open: true
		});
	};
	handleClose = () => {
		this.setState({
			open: false
		});
	};

	handleEditQuiz = id => {
		this.props.history.push("/quiz/" + id);
	};
	handleScheduleQuiz = id => {
		this.props.history.push("/schedule-quiz/" + id);
	};

	columns = [
		{ title: "Title", field: "title" },
		{
			title: "Skills",
			field: "skills",
			render: rowData =>
				rowData.skills.map(skill => {
					return <li>{skill}</li>;
				})
		},
		{
			title: "Number Of Questions",
			field: "numberOfQuestions",
			type: "numeric"
		},
		{
			title: "Scheduling Status",
			field: "schedulingStatus",
			lookup: { false: "Not Scheduled", true: "Scheduled" }
		},
		{
			title: "Id",
			field: "id",
			hidden: true
		}
	];

	data = [
		{
			title: "Python",
			skills: ["Data Structures", "Algos", "Conceptual"],
			numberOfQuestions: 1,
			schedulingStatus: true,
			id: "1"
		},
		{
			title: "Python",
			skills: ["Data Structures", "Algos", "Conceptual"],
			numberOfQuestions: 5,
			schedulingStatus: false,
			id: "2"
		},
		{
			title: "Python",
			skills: ["Data Structures", "Algos", "Conceptual"],
			numberOfQuestions: 1,
			schedulingStatus: true,
			id: "3"
		},
		{
			title: "Python",
			skills: ["Data Structures", "Algos", "Conceptual"],
			numberOfQuestions: 1,
			schedulingStatus: false,
			id: "4"
		}
	];

	actions = [
		{
			icon: "edit",
			tooltip: "Edit Content",
			onClick: (event, rowData) => {
				console.log(rowData);
				return this.handleEditQuiz(rowData.id);
			}
		},
		rowData => ({
			icon: "schedule",
			tooltip: "Schedule Quiz",
			onClick: (event, rowData) => {
				console.log(rowData);
				return this.handleScheduleQuiz(rowData.id);
			},
			disabled: rowData.numberOfQuestions < 5
		}),
		{
			icon: "delete_outline",
			tooltip: "Delete Quiz",
			onClick: (event, rowData) => {
				alert("Quiz deleted");
			}
		}
	];

	render() {
		const { classes } = this.props;

		return (
			<DashboardLayout title="Quizzes">
				<div className={classes.root}>
					<QuizToolbar handleClickOpen={this.handleClickOpen} />
					{this.state.open && (
						<SendContent
							open={this.state.open}
							handleClose={this.handleClose}
							mode={this.state.mode}
							data={this.state.editData}
						/>
					)}
					<div className={classes.content}>
						<MaterialTable
							columns={this.columns}
							data={this.data}
							options={{
								filtering: true,
								actionsColumnIndex: -1
							}}
							actions={this.actions}
							title="Quiz List"
						/>
					</div>
				</div>
			</DashboardLayout>
		);
	}
}

export default withStyles(styles)(QuizList);
