import React, { Component } from "react";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import {
	Portlet,
	PortletHeader,
	PortletLabel,
	PortletToolbar,
	PortletContent,
	PortletFooter
} from "components";
import { withStyles } from "@material-ui/styles";
import styles from "./styles";

class Team extends Component {
	constructor(props) {
		super(props);
		this.state = {};
	}

	render() {
		return (
			<div className={this.props.classes.root}>
				<Grid container spacing={2}>
					<Typography variant="h2">Team Members :</Typography>
					{this.props.teamMembers.map(teamMember => {
						return (
							<Grid item md={6}>
								<Portlet className={this.props.classes.portlet}>
									<PortletContent>
										<Typography
											variant="h3"
											className={this.props.classes.title}
											color="textPrimary"
											gutterBottom
										/>
										<Typography
											variant="h4"
											className={this.props.classes.subtitle}
											color="textPrimary"
											gutterBottom
										/>
									</PortletContent>
									<PortletFooter />
								</Portlet>
							</Grid>
						);
					})}
				</Grid>
				<br />
				<Grid container spacing={2}>
					<Typography variant="h2">Mentors :</Typography>
					{this.props.faculties.map(faculty => {
						return (
							<Grid item md={6}>
								<Portlet className={this.props.classes.portlet}>
									<PortletContent>
										<Typography
											variant="h3"
											className={this.props.classes.title}
											color="textPrimary"
											gutterBottom
										/>
										<Typography
											variant="h4"
											className={this.props.classes.subtitle}
											color="textPrimary"
											gutterBottom
										/>
									</PortletContent>
									<PortletFooter />
								</Portlet>
							</Grid>
						);
					})}
				</Grid>
			</div>
		);
	}
}
const mapStatetoProps = (state, props) => {
	let facuties = [];

	let teamMembers = [];
	state.project.projectlistTabledata.map(project => {
		if (project.id === props.match.params.projectId) {
			faculties = project.faculties;
			teamMembers = project.teamMembers;
		}
	});

	return {
		title: title,
		faculties: faculties,
		teamMembers: teamMembers
	};
};

export default connect(mapStatetoProps)(withStyles(styles)(Team));
