import React, { Component } from "react";

// Externals
import PropTypes from "prop-types";

// Material helpers
import { withStyles } from "@material-ui/styles";

// Material components
import { Grid } from "@material-ui/core";

// Shared layouts
import { Dashboard as DashboardLayout } from "layouts";

import Loader from "components/loader";
import { fetchDashboardData } from "api/dashboard";
import { connect } from "react-redux";

// Custom components
import {
	Content,
	Quizzes,
	Projects,
	Engagement,
	StudentCategorization,
	ProjectCompletionBargraph,
	ProductList,
	OrdersTable
} from "./components";
import swal from "sweetalert";
import { DashboardData } from "serializers/dashboard";

// Component styles
const styles = theme => ({
	root: {
		padding: theme.spacing(4)
	},
	item: {
		height: "100%"
	}
});

class Dashboard extends Component {
	constructor(props) {
		super(props);
		this.state = {
			isLoading: true
		};
	}

	componentDidMount() {
		this.loadData();
	}

	loadData = () => {
		fetchDashboardData(this.props.token)
			.then(data => {
				let dashboardData = new DashboardData(data.dashBoardData);
				this.setState({
					...dashboardData,
					isLoading: false
				});
			})
			.catch(error => {
				swal({
					title: "Internal Server Error",
					text: "Please Try Refreshing Page",
					button: "Ok"
				});
			});
	};

	render() {
		const { classes } = this.props;

		return (
			<DashboardLayout title="Dashboard">
				<div className={classes.root}>
					{this.state.isLoading ? (
						<Loader />
					) : (
						<Grid container spacing={4}>
							<Grid item lg={3} sm={6} xl={3} xs={12}>
								<Content
									className={classes.item}
									content={this.state.content}
								/>
							</Grid>
							<Grid item lg={3} sm={6} xl={3} xs={12}>
								<Quizzes
									className={classes.item}
									quizzes={this.state.quizzes}
								/>
							</Grid>
							<Grid item lg={3} sm={6} xl={3} xs={12}>
								<Projects
									className={classes.item}
									projects={this.state.noOfProjects}
								/>
							</Grid>
							<Grid item lg={3} sm={6} xl={3} xs={12}>
								<Engagement className={classes.item} />
							</Grid>
							<Grid item lg={8} md={12} xl={9} xs={12}>
								<ProjectCompletionBargraph
									className={classes.item}
									data={this.state.projectGraphValues}
									labels={this.state.projectGraphLabels}
								/>
							</Grid>
							<Grid item lg={4} md={6} xl={3} xs={12}>
								<StudentCategorization className={classes.item} />
							</Grid>

							<Grid item lg={4} md={6} xl={3} xs={12}>
								<ProductList className={classes.item} />
							</Grid>
							<Grid item lg={8} md={12} xl={9} xs={12}>
								<OrdersTable className={classes.item} />
							</Grid>
						</Grid>
					)}
				</div>
			</DashboardLayout>
		);
	}
}

Dashboard.propTypes = {
	classes: PropTypes.object.isRequired
};

const mapStateToProps = state => {
	return {
		token: state.auth.token
	};
};

export default connect(mapStateToProps)(withStyles(styles)(Dashboard));
