import React, { Component } from "react";
import MaterialTable from "material-table";
import styles from "./styles";
import { withStyles } from "@material-ui/styles";
import { connect } from "react-redux";
import { loadStudentList } from "api/classroom";
import Student from "views/Student";
import { Loader } from "components";

class StudentsList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {
				students: [
					{
						name: "Rchel",
						course: "MBA",
						year: 1,
						branch: "CSE",
						className: "CSE 2A",
						id: 1
					},
					{
						name: "Rchel",
						course: "MBA",
						year: 1,
						branch: "CSE",
						className: "CSE 2A",
						id: 2
					},
					{
						name: "Rchel",
						course: "MBA",
						year: 1,
						branch: "CSE",
						className: "CSE 2A",
						id: 3
					},
					{
						name: "Rchel",
						course: "MBA",
						year: 1,
						branch: "CSE",
						className: "CSE 2A",
						id: 4
					},
					{
						name: "Rchel",
						course: "MBA",
						year: 1,
						branch: "CSE",
						className: "CSE 2A",
						id: 5
					},

					{
						name: "Rchel",
						course: "MBA",
						year: 1,
						branch: "CSE",
						className: "CSE 2A",
						id: 6
					},
					{
						name: "Rchel",
						course: "MBA",
						year: 1,
						branch: "CSE",
						className: "CSE 2A",
						id: 7
					}
				]
			}
		};
	}

	componentDidMount() {
		this.loadData();
	}

	loadData = () => {
		loadStudentList(this.props.token, this.props.match.params.classroomId)
			.then(data => {
				let students = data.students.map(student => {
					return new Student(student);
				});

				this.setState(prevState => {
					return {
						data: {
							students
						},
						isLoading: false
					};
				});
			})
			.catch(error => {
				console.log(error);
			});
	};

	columns = [
		{ title: "Name", field: "name" },
		{
			title: "Course",
			field: "course"
		},
		{
			title: "Year",
			field: "year",
			type: "numeric"
		},
		{
			title: "Branch",
			field: "branch"
		},
		{
			title: "Class Name",
			field: "className"
		},

		{
			title: "Id",
			field: "id",
			hidden: true
		}
	];
	actions = [
		{
			icon: "visibility",
			tooltip: "View Student Report ",
			onClick: (event, rowData) => {
				console.log(rowData);
				this.props.history.push("/student/" + rowData.id + "/dashboard");
			}
		}
	];

	render() {
		return (
			<div className={this.props.classes.root}>
				<MaterialTable
					columns={this.columns}
					data={this.state.data.students}
					options={{
						filtering: true,
						actionsColumnIndex: -1
					}}
					actions={this.actions}
					title="Students List"
				/>
			</div>
		);
	}
}

const MapStatetoProps = state => {
	return {
		token: state.auth.token
	};
};

export default connect(MapStatetoProps)(withStyles(styles)(StudentsList));
