import React, { PureComponent } from "react";
import { Pie } from "react-chartjs-2";
import styles from "./styles";
import { withStyles } from "@material-ui/styles";
import palette from "theme/palette";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import Bydifficultylevel from "./components/Bydifficultylevel";
import BySkillSet from "./components/BySkillSet";
class Visualization extends PureComponent {
	constructor(props) {
		super(props);
		this.state = Visualization.getVisualizationData(props.questions);
	}
	static getVisualizationData = questions => {
		console.log("am settting state");
		let easy = 0;
		let medium = 0;
		let hard = 0;

		questions.map(question => {
			switch (question.difficultyLevel) {
				case "easy":
					++easy;
					break;
				case "medium":
					++medium;
					break;
				case "hard":
					++hard;
			}
		});

		return {
			easy,
			medium,
			hard
		};
	};

	static getDerivedStateFromProps(props, state) {
		//check if props.questions has changed
		let data = Visualization.getVisualizationData(props.questions);
		if (
			data.easy === state.easy &&
			data.medium === state.medium &&
			data.hard === state.hard
		) {
			return null;
		} else {
			return data;
		}
	}

	render() {
		return (
			<Grid container spacing={2}>
				<Grid item md={6}>
					<Bydifficultylevel />
				</Grid>
				<Grid item md={6}>
					<BySkillSet />
				</Grid>
			</Grid>
		);
	}
}

export default withStyles(styles)(Visualization);
