import React, { Component } from "react";
import { withStyles } from "@material-ui/styles";
import styles from "./styles";

class Projects extends Component {
	constructor(props) {
		super(props);
		this.state = {
			projects: [
				{
					Id: 1,
					title: "Smart Water Supply",
					skills: ["Machine Learing", "Reactjs"],
					team: "ASSAS",
					facultylead: "MR. Jai Prakash"
				},
				{
					Id: 2,
					title: "E learning Management",
					skills: ["Nodejs", "Angular"],
					team: "REREF",
					facultylead: "Mr. Deepak"
				},
				{
					Id: 3,
					title: "Content Management",
					skills: ["Firebase", "HTML", "CSS"],
					team: "FEFE",
					facultylead: "Ms. Mansha "
				},
				{
					Id: 4,
					title: "Sadda College",
					skills: ["Bootstrap", "Photoshop", "Material Design"],
					team: "FRE",
					facultylead: "Mr. Shubham"
				}
			]
		};
	}

	render() {
		const classes = this.props.classes;
		return (
			<div className={classes.root}>
				<Grid container spacing={2}>
					{this.state.classrooms.map(classroom => {
						return (
							<Grid item md={6} className={classes.splitFormSpacing}>
								<Portlet className={classes.portlet}>
									<PortletContent>
										<Typography
											variant="h3"
											className={classes.title}
											color="textPrimary"
											gutterBottom>
											{classroom.name}
										</Typography>
										<Typography
											variant="h4"
											className={classes.subtitle}
											color="textPrimary"
											gutterBottom>
											{classroom.cousrename}
										</Typography>
									</PortletContent>
									<PortletFooter>
										<Button size="small" variant="outlined">
											<Link to={"/class-room/" + classroom.Id + "/details"}>
												Know More
											</Link>
										</Button>
									</PortletFooter>
								</Portlet>
							</Grid>
						);
					})}
				</Grid>
			</div>
		);
	}
}
export default withStyles(styles)(Projects);
