import React, { Component } from "react";

import { Dashboard as DashboardLayout } from "layouts";

import StudymaterialToolbar from "./components/StudymaterialToolbar";
import SendContent from "./components/SendContent";
import MaterialTable from "material-table";
import Dialog from "@material-ui/core/Dialog";

// styles
import styles from "./styles";
import { withStyles } from "@material-ui/styles";

class StudymaterialList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			open: false,
			mode: "Send",
			editData: null
		};
	}

	handleClickOpen = (mode = "Send", editData = null) => {
		this.setState({
			open: true,
			mode,
			editData
		});
	};
	handleClose = () => {
		this.setState({
			open: false
		});
	};

	columns = [
		{ title: "Title", field: "title" },
		{ title: "Description", field: "description" },
		{ title: "Type", field: "type" },
		{
			title: "Year",
			field: "year"
		},
		{
			title: "Url",
			field: "url",
			hidden: true
		}
	];

	data = [
		{
			title: "Mehmet",
			description: "Baran",
			type: "hgjgjh",
			year: "1",
			url: "https://material-ui.com/"
		},
		{
			title: "Mehmet",
			description: "Baran",
			type: "vhjjh",
			year: "2",
			url: "https://material-ui.com/"
		},
		{
			title: "Mehmet",
			description: "Baran",
			type: "cxcxc",
			year: "3",
			url: "https://material-ui.com/"
		},
		{
			title: "Mehmet",
			description: "Baran",
			type: "sdsddcsc",
			year: "4",
			url: "https://material-ui.com/"
		}
	];

	actions = [
		{
			icon: "visibility",
			tooltip: "View Content",
			onClick: (event, rowData) => {
				var win = window.open(rowData.url, "_blank");
				win.focus();
			}
		},
		{
			icon: "edit",
			tooltip: "Edit Content",
			onClick: (event, rowData) => {
				return this.handleClickOpen();
			}
		}
	];

	render() {
		const { classes } = this.props;

		return (
			<DashboardLayout title="Studymaterial">
				<div className={classes.root}>
					<StudymaterialToolbar handleClickOpen={this.handleClickOpen} />
					<Dialog
						onClose={this.handleClose}
						open={this.state.open}
						disableBackdropClick="true">
						<SendContent handleClose={this.handleClose} />
					</Dialog>
					<MaterialTable
						className={classes.content}
						columns={this.columns}
						data={this.data}
						options={{
							filtering: true,
							actionsColumnIndex: -1
						}}
						actions={this.actions}
						title="Study Material List"
					/>
				</div>
			</DashboardLayout>
		);
	}
}

export default withStyles(styles)(StudymaterialList);
