import React, { Component } from "react";

import { Dashboard as DashboardLayout } from "layouts";

import StudymaterialToolbar from "./components/StudymaterialToolbar";
import SendContent from "./components/SendContent";
import MaterialTable from "material-table";
import Dialog from "@material-ui/core/Dialog";

import { SvgIconProps } from "@material-ui/core/SvgIcon";

import Search from "@material-ui/icons/Search";
import ViewColumn from "@material-ui/icons/ViewColumn";
import SaveAlt from "@material-ui/icons/SaveAlt";
import ChevronLeft from "@material-ui/icons/ChevronLeft";
import ChevronRight from "@material-ui/icons/ChevronRight";
import FirstPage from "@material-ui/icons/FirstPage";
import LastPage from "@material-ui/icons/LastPage";
import Add from "@material-ui/icons/Add";
import Check from "@material-ui/icons/Check";
import FilterList from "@material-ui/icons/FilterList";
import Remove from "@material-ui/icons/Remove";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";

import moduleName from "module";
// styles
import styles from "./styles";
import { withStyles } from "@material-ui/styles";

class StudymaterialList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			open: false
		};
	}

	handleClickOpen = () => {
		this.setState({
			open: true
		});
	};
	handleClose = () => {
		this.setState({
			open: false
		});
	};

	columns = [
		{ title: "Title", field: "title" },
		{ title: "Description", field: "description" },
		{ title: "Type", field: "type" },
		{
			title: "Year",
			field: "year"
		},
		{
			title: "Url",
			field: "url",
			hidden: true
		}
	];

	data = [
		{
			title: "Mehmet",
			description: "Baran",
			type: "hgjgjh",
			year: "1",
			url: "https://material-ui.com/"
		},
		{
			title: "Mehmet",
			description: "Baran",
			type: "vhjjh",
			year: "2",
			url: "https://material-ui.com/"
		},
		{
			title: "Mehmet",
			description: "Baran",
			type: "cxcxc",
			year: "3",
			url: "https://material-ui.com/"
		},
		{
			title: "Mehmet",
			description: "Baran",
			type: "sdsddcsc",
			year: "4",
			url: "https://material-ui.com/"
		}
	];

	actions = [
		{
			icon: "visibility",
			tooltip: "View Content",
			onClick: (event, rowData) => {
				var win = window.open(rowData.url, "_blank");
				win.focus();
			}
		},
		{
			icon: "edit",
			tooltip: "Edit Content",
			onClick: (event, rowData) => {
				<SendContent
					mode="Edit"
					data={rowData}
					handleClose={this.handleClose}
				/>;
			}
		}
	];

	render() {
		const { classes } = this.props;

		return (
			<DashboardLayout title="Studymaterial">
				<div className={classes.root}>
					<StudymaterialToolbar handleClickOpen={this.handleClickOpen} />
					<Dialog
						onClose={this.handleClose}
						open={this.state.open}
						disableBackdropClick="true">
						<SendContent handleClose={this.handleClose} mode="Send" />
					</Dialog>
					<MaterialTable
						className={classes.content}
						columns={this.columns}
						data={this.data}
						options={{
							filtering: true,
							actionsColumnIndex: -1
						}}
						actions={this.actions}
						title="Study Material List"
					/>
				</div>
			</DashboardLayout>
		);
	}
}

export default withStyles(styles)(StudymaterialList);
