import React, { Component } from "react";
import ContentForm from "./Form";
import validate from "validate.js";
import { Redirect } from "react-router-dom";
import { createQuiz } from "api/quizzes";
import { connect } from "react-redux";
import { Quiz } from "serializers/quiz";
import swal from "sweetalert";
class CreateQuiz extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {
				title: "",
				skills: [],
				text: "",
				warning: null,
				redirect: false,
				quizid: ""
			},
			isLoading: false,
			errors: {}
		};
	}

	onChangeHandler = e => {
		const { name, value } = e.target;

		this.setState(prevState => {
			return {
				data: {
					...prevState.data,
					[name]: value
				},
				errors: {
					...prevState.errors,
					[name]: null
				}
			};
		});
	};

	onAddHandler = e => {
		e.preventDefault();

		// check for repitition
		if (this.state.data.text) {
			var found = false;
			for (var i = 0; i < this.state.data.skills.length; i++) {
				if (this.state.data.skills[i].text === this.state.data.text) {
					found = true;
					break;
				}
			}

			if (found === false) {
				const newItem = {
					text: this.state.data.text,
					id: Date.now()
				};
				this.setState(prevState => ({
					data: {
						...prevState.data,
						skills: prevState.data.skills.concat(newItem),
						text: "",
						warning: null
					}
				}));
			} else {
				this.setState(prevState => ({
					data: {
						...prevState.data,
						warning: "Already added"
					}
				}));
			}
		}
	};

	OnDeleteHandler = itemid => {
		let skills = [...this.state.data.skills];
		let idx = skills.indexOf(itemid);
		skills.splice(idx, 1);
		this.setState(prevState => ({
			data: {
				...prevState.data,
				skills: [...skills]
			}
		}));
	};

	onSubmitHandler = () => {
		//validation
		let errors = validate(this.state.data, {
			title: { presence: { allowEmpty: false, message: "is required" } },
			skills: { presence: { allowEmpty: false, message: "are required" } }
		});

		// check for errors
		if (errors) {
			this.setState({ errors });
		} else {
			this.setState({
				isLoading: true
			});
			let data = {
				title: this.state.data.title,
				skills: this.state.data.skills.map(skill => {
					return skill.text;
				})
			};
			createQuiz(this.props.token, data)
				.then(data => {
					this.setState(prevState => ({
						data: {
							...prevState.data,
							quizid: data.quiz.id,
							redirect: true
						}
					}));
					this.props.pushNewQuiz(data.quiz);
				})
				.catch(error => {
					swal({
						title: "Error",
						text: error.response.data.message,

						button: "Ok"
					});
					this.setState({
						isLoading: false
					});
				});
		}
	};

	render() {
		let redirect = null;
		if (this.state.data.redirect) {
			redirect = (
				<Redirect to={"/quiz/" + this.state.data.quizid + "/edit-quiz"} />
			);
		}

		return (
			<div>
				{redirect}
				<ContentForm
					open={this.props.open}
					onChangeHandler={this.onChangeHandler}
					onSubmit={this.onSubmitHandler}
					errors={this.state.errors}
					data={this.state.data}
					onAdd={this.onAddHandler}
					onDelete={this.OnDeleteHandler}
					handleClose={this.props.handleClose}
					isLoading={this.state.isLoading}
				/>
			</div>
		);
	}
}
const mapStateToProps = state => {
	return {
		token: state.auth.token
	};
};

const mapDispatchToProps = dispatch => {
	return {
		pushNewQuiz: Newquiz =>
			dispatch({
				type: "PUSH NEW QUIZ",
				newquiz: new Quiz(Newquiz)
			})
	};
};

export default connect(
	mapStateToProps,
	mapDispatchToProps
)(CreateQuiz);
