const initialState = {
	quizlistTabledata: []
};

const reducer = (state = initialState, action = {}) => {
	switch (action.type) {
		case "PUSH QUIZLIST DATA":
			console.log("hey am redux");
			console.log(action.data);
			return {
				quizlistTabledata: action.data
			};
			break;
		case "UPDATE SCHEDULE STATUS":
			console.log("hey am redux");
			state.quizlistTabledata.map(quiz => {
				if (quiz.id === action.quizId) {
					quiz.schedulingStatus = action.updatedStatus;
				}
				return quiz;
			});
			return {
				quizlistTabledata: action.data
			};
			break;
		default:
			return state;
	}
};

export default reducer;
