import React from "react";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";

import Button from "@material-ui/core/Button";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import Dialog from "@material-ui/core/Dialog";
import DialogTitle from "@material-ui/core/DialogTitle";

import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import Fab from "@material-ui/core/Fab";
import AddIcon from "@material-ui/icons/Add";
import InputAdornment from "@material-ui/core/InputAdornment";

import DeleteIcon from "@material-ui/icons/Delete";
import { makeStyles } from "@material-ui/styles";
import LinearProgress from "@material-ui/core/LinearProgress";

const useStyles = makeStyles(theme => ({
	closeButton: {
		position: "absolute",
		right: theme.spacing(1),
		top: theme.spacing(1),
		color: theme.palette.grey[500]
	},
	splitFormSpacing: {
		marginTop: "16px",
		marginBottom: "8px"
	}
}));

const EditEndDate = props => {
	const classes = useStyles();

	return (
		<Dialog
			onClose={props.handleClose}
			open={props.open}
			fullWidth
			maxWidth="sm"
			disableBackdropClick={true}>
			<DialogTitle>
				<Typography variant="h3">Extend End Date</Typography>

				<IconButton
					className={classes.closeButton}
					onClick={props.handleClose}
					disabled={props.isLoading}>
					<CloseIcon />
				</IconButton>
			</DialogTitle>
			<DialogContent dividers>
				{props.isLoading ? <LinearProgress /> : null}
				<Grid className={classes.splitFormSpacing} item xs={12}>
					<TextField
						value={props.data.enddate}
						label="End Date"
						variant="outlined"
						fullWidth
						name="enddate"
						onChange={props.onChangeHandler}
						required={true}
						error={props.errors.enddate}
					/>
					<Typography color="error" variant="caption">
						{props.errors.enddate && props.errors.enddate[0]}
					</Typography>
				</Grid>
			</DialogContent>

			<DialogActions>
				<Button
					variant="contained"
					color="primary"
					onClick={props.onSubmit}
					disabled={props.isLoading}>
					Create Quiz
				</Button>
			</DialogActions>
		</Dialog>
	);
};

class Skillsadded extends React.Component {
	render() {
		return (
			<div>
				<Typography variant="h4">Skills To Be Tested :</Typography>
				<ul>
					{this.props.skills.map(skill => (
						<li key={skill.id}>
							{skill.text}{" "}
							<IconButton
								aria-label="Delete"
								onClick={() => this.props.deleteHandler(skill.id)}>
								<DeleteIcon />
							</IconButton>
						</li>
					))}
				</ul>
			</div>
		);
	}
}

export default CreateQuiz;
