import React, { Component } from "react";
import { Pie } from "react-chartjs-2";
import styles from "./styles";
import { withStyles } from "@material-ui/styles";
import palette from "theme/palette";
import Typography from "@material-ui/core/Typography";
import {
	Portlet,
	PortletHeader,
	PortletLabel,
	PortletToolbar,
	PortletContent
} from "components";
class Visualization extends Component {
	data = {
		datasets: [
			{
				data: [6, 15, 22],
				backgroundColor: [
					palette.primary.main,
					palette.danger.main,
					palette.warning.main
				],
				borderWidth: 8,
				borderColor: palette.common.white,
				hoverBorderColor: palette.common.white
			}
		],
		labels: ["Easy", "Medium", "Hard"]
	};

	options = {
		legend: {
			display: false
		},
		responsive: true,
		maintainAspectRatio: false,
		animation: false,
		cutoutPercentage: 80,
		layout: { padding: 0 },
		tooltips: {
			enabled: true,
			mode: "index",
			intersect: false,
			borderWidth: 1,
			borderColor: palette.border,
			backgroundColor: palette.common.white,
			titleFontColor: palette.text.primary,
			bodyFontColor: palette.text.secondary,
			footerFontColor: palette.text.secondary
		}
	};

	render() {
		return (
			<div className={this.props.classes.chartWrapper}>
				<Portlet>
					<PortletHeader noDivider>
						<PortletLabel title="Questions By Difficulty Level" />
					</PortletHeader>
					<PortletContent>
						<div className={this.props.classes.chartWrapper}>
							<Pie data={this.data} options={this.options} />
						</div>
						<div className={this.props.classes.stats}>
							<div className={this.props.classes.device}>
								<Typography variant="body1">Easy</Typography>
								<Typography
									style={{ color: palette.primary.main }}
									variant="h2">
									63%
								</Typography>
							</div>
							<div className={this.props.classes.device}>
								<Typography variant="body1">Medium</Typography>
								<Typography style={{ color: palette.danger.main }} variant="h2">
									15
								</Typography>
							</div>
							<div className={this.props.classes.device}>
								<Typography variant="body1">Hard</Typography>
								<Typography
									style={{ color: palette.warning.main }}
									variant="h2">
									22
								</Typography>
							</div>
						</div>
					</PortletContent>
				</Portlet>
			</div>
		);
	}
}

export default withStyles(styles)(Visualization);
