import React, { Component } from "react";

// Externals
import PropTypes from "prop-types";

// Material helpers
import { withStyles } from "@material-ui/styles";

// Material components
import { Grid } from "@material-ui/core";

// Shared layouts
import { Dashboard as DashboardLayout } from "layouts";

import { connect } from "react-redux";

import { makeStyles } from "@material-ui/core/styles";

// Custom components

import swal from "sweetalert";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import OutlinedInput from "@material-ui/core/OutlinedInput";
// Component styles
const styles = theme => ({
  root: {
    padding: theme.spacing(4)
  },
  item: {
    height: "100%"
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120
  },
  selectEmpty: {
    marginTop: theme.spacing(2)
  }
});

class Dashboard extends Component {
  constructor(props) {
    super(props);
    this.state = {
      ur: 0,
      cgpa: 0
    };
  }
  handleChange = e => {
    const { name, value } = e.target;
    this.setState(prevState => {
      return {
        ...prevState,
        [name]: value
      };
    });
  };
  render() {
    const { classes } = this.props;

    return (
      <DashboardLayout title="Dashboard">
        <div className={classes.root}>
          <FormControl variant="outlined" fullWidth>
            <InputLabel>University Rating</InputLabel>
            <Select
              value={this.props.ur}
              onChange={e => this.props.updateur(e.target.value)}
              name="ur"
              input={<OutlinedInput name="ur" labelWidth={120} />}
            >
              ><MenuItem value={1}>1</MenuItem>
              <MenuItem value={2}>2</MenuItem>
              <MenuItem value={3}>3</MenuItem>
              <MenuItem value={4}>4</MenuItem>
              <MenuItem value={5}>5</MenuItem>
            </Select>
          </FormControl>
          <br />
          <br />
          <br />
          <br />
          <FormControl variant="outlined" fullWidth>
            <InputLabel>CGPA</InputLabel>
            <Select
              value={this.props.cgpa}
              onChange={e => this.props.updatecgpa(e.target.value)}
              name="cgpa"
              input={<OutlinedInput name="cgpa" labelWidth={50} />}
            >
              ><MenuItem value={1}>1</MenuItem>
              <MenuItem value={2}>2</MenuItem>
              <MenuItem value={3}>3</MenuItem>
              <MenuItem value={4}>4</MenuItem>
              <MenuItem value={5}>5</MenuItem>
              <MenuItem value={6}>6</MenuItem>
              <MenuItem value={7}>7</MenuItem>
              <MenuItem value={8}>8</MenuItem>
              <MenuItem value={9}>9</MenuItem>
              <MenuItem value={10}>10</MenuItem>
            </Select>
          </FormControl>
        </div>
      </DashboardLayout>
    );
  }
}

Dashboard.propTypes = {
  classes: PropTypes.object.isRequired
};

const mapStateToProps = state => {
  return {
    token: state.auth.token,
    ur: state.dashboard.ur,
    cgpa: state.dashboard.cgpa,
    aptitudescore: state.dashboard.aptitudescore
  };
};
const mapDispatchToProps = dispatch => {
  return {
    updateur: data => dispatch({ type: "UPDATE UR", data: data }),
    updatecgpa: data => dispatch({ type: "UPDATE CGPA", data: data })
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withStyles(styles)(Dashboard));
