    // const { store } = this.props;
    // const { grid, status } = store.getState();
    const grid = this.props.grid;
    console.log(grid);
    console.log("hey");
    const isSolved = this.props.isSolved;
    const isEdited = this.props.isEdited;
    // const { isSolved, isEdited } = status;
    return (
      <DashboardLayout mode={APTITUDE} title="Quizzes">
        <div className={classes.root}>
          <div>
            {/* <button
              className="undo"
              disabled={window.gridHistory && !window.gridHistory.length}
              onClick={this.props.undo()}
            >
              ⤺ Undo
            </button> */}
            {/* <button
              className="clear"
              disabled={!isEdited}
              onClick={this.props.clear()}
            >
              ⟲ Clear
            </button> */}

            <Grid grid={grid} isSolved={isSolved} {...this.props} />

            {/* <button
              className="check"
              disabled={isSolved}
              onClick={() => {
                if (isSolvable({ grid })) {
                  if (isComplete({ grid })) {
                    return alert("Congratulations, you solved it!!");
                  }
                  alert("This Sudoku is solvable, keep going !!");
                } else {
                  alert("This Sudoku is NOT solvable");
                }
              }}
            >
              Check
            </button> */}
            {/* <button className="solve" onClick={this.props.solve()}>
              Solve
            </button> */}
            <div className="footnote">
              <p>
                by <a href="http://danialk.github.io/"> Danial Khosravi </a>{" "}
              </p>
            </div>
          </div>
        </div>
      </DashboardLayout>
    );
  }
}
const mapStateToProps = state => {
  return {
    token: state.auth.token,
    grid: state.sudokugrid.grid,
    isSolved: state.sudokustatus.isSolved,
    isEdited: state.sudokustatus.isEdited
  };
};

const mapDispatchToProps = dispatch => {
  return {
    undo: () => dispatch({ type: "UNDO" }),
    clear: () => dispatch({ type: "CLEAR" }),
    solve: () => dispatch({ type: "SOLVE" })
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withStyles(styles)(Sudoku));
