import React, { Component } from "react";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";

import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

import moment from "moment";

import styles from "./styles";
import { withStyles } from "@material-ui/styles";
import validate from "validate.js";

class schedulequiz extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {
				quiztitle: "Python",
				schedulingStatus: "Scheduled",
				startdate: "",
				enddate: "",
				timelimit: ""
			},
			error: null
		};
	}

	startdateHandler = e => {
		this.setState(prevState => {
			return { data: { ...prevState.data, startdate: e.target.value } };
		});
	};

	enddateHandler = e => {
		this.setState(prevState => {
			return { data: { ...prevState.data, enddate: e.target.value } };
		});
	};

	timelimitHandler = e => {
		this.setState(prevState => {
			return { data: { ...prevState.data, timelimit: e.target.value } };
		});
	};

	schedulequizHandler = () => {
		// validation of user input
	};

	render() {
		return (
			<div className={this.props.classes.root}>
				<Typography style={{ textAlign: "center" }} variant="h3">
					{this.state.data.quiztitle}
				</Typography>
				<Typography style={{ textAlign: "center" }} variant="subtitle1">
					Scheduling Status: {this.state.data.schedulingStatus}
				</Typography>
				<Grid container spacing={2} className={this.props.classes.content}>
					<Grid item md={4}>
						<Typography>Start Date</Typography>
						<TextField
							type="date"
							defaultValue="2017-05-24"
							onChange={this.startdateHandler}
							value={this.state.data.startdate}
						/>
						<br />
						<Typography variant="caption">Day when quiz begins</Typography>
					</Grid>
					<Grid item md={4}>
						<Typography>End Date</Typography>
						<TextField
							type="date"
							defaultValue="2017-05-24"
							onChange={this.enddateHandler}
							value={this.state.data.enddate}
						/>
						<br />
						<Typography variant="caption">Last day to give quiz</Typography>
					</Grid>
					<Grid item md={4}>
						<Typography>Time limit</Typography>

						<TextField
							label="Number"
							type="number"
							onChange={this.timelimitHandler}
							value={tjis.state.data.timelimit}
						/>
						<br />
						<Typography variant="caption">(in seconds)</Typography>
					</Grid>
				</Grid>

				<Button
					variant="contained"
					color="primary"
					onClick={this.schedulequizHandler}>
					{this.state.data.schedulingStatus === "Scheduled"
						? "Reschedule Quiz"
						: "Schedule Quiz"}
				</Button>
			</div>
		);
	}
}

export default withStyles(styles)(schedulequiz);
