import React, { Component } from "react";
import ContentForm from "./Form";
import validate from "validate.js";

class SendContent extends Component {
	constructor(props) {
		super(props);

		this.state = this.getInitialState(props);
	}

	receipents = ["All Years", "1st Year", "2nd Year", "3rd Year", "4th Year"];
	types = ["Article", "Video", "Research Paper", "Assignment"];

	static defaultProps = {
		mode: "Send"
	};

	getInitialState = props => {
		if (props.mode === "Edit") {
			return {
				data: {
					title: "",
					description: "",
					url: "",
					type: "",
					year: "",
					file: "",
					...props.data
				},
				errors: {}
			};
		} else {
			// default mode is assumed to be Send
			return {
				data: {
					title: "",
					description: "",
					url: "",
					type: "",
					year: "",
					file: ""
				},
				errors: {}
			};
		}
	};

	onChangeHandler = e => {
		const { name, value } = e.target;

		this.setState(prevState => {
			return {
				data: {
					...prevState.data,
					[name]: value
				},
				errors: {
					...prevState.errors,
					[name]: null
				}
			};
		});
	};

	onSubmitHandler = () => {
		console.log("submission handler");
		//validation
		let errors = validate(this.state.data, {
			title: { presence: { allowEmpty: false, message: "is required" } },
			description: { presence: { allowEmpty: false, message: "is required" } },
			url: { url: true },
			type: { presence: { allowEmpty: false, message: "is required" } },
			year: { presence: { allowEmpty: false, message: "is required" } },
			file: { presence: { allowEmpty: false, message: "is required" } }
		});

		// check for errors
		if (errors) {
			this.setState({ errors });
		}
		console.log(errors);
	};

	render() {
		return (
			<ContentForm
				open={this.props.open}
				onChangeHandler={this.onChangeHandler}
				onSubmit={this.onSubmitHandler}
				errors={this.state.errors}
				data={this.state.data}
				years={this.years}
				types={this.types}
				handleClose={this.props.handleClose}
			/>
		);
	}
}

export default SendContent;
