import React from "react";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";

import Button from "@material-ui/core/Button";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import Dialog from "@material-ui/core/Dialog";
import DialogTitle from "@material-ui/core/DialogTitle";

import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import OutlinedInput from "@material-ui/core/OutlinedInput";

import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import { makeStyles } from "@material-ui/styles";
import Checkbox from "@material-ui/core/Checkbox";

const useStyles = makeStyles(theme => ({
	closeButton: {
		position: "absolute",
		right: theme.spacing(1),
		top: theme.spacing(1),
		color: theme.palette.grey[500]
	},
	splitFormSpacing: {
		marginTop: "16px",
		marginBottom: "8px"
	}
}));

const SendContent = props => {
	const classes = useStyles();

	return (
		<Dialog
			onClose={props.handleClose}
			open={props.open}
			fullWidth
			maxWidth="md"
			disableBackdropClick={true}>
			<DialogTitle>
				<Typography variant="h3"> {props.mode} QUESTION</Typography>

				<IconButton className={classes.closeButton} onClick={props.handleClose}>
					<CloseIcon />
				</IconButton>
			</DialogTitle>
			<DialogContent dividers>
				<Grid className={classes.splitFormSpacing} item xs={12}>
					<TextField
						value={props.data.questiontitle}
						label="Question"
						variant="outlined"
						fullWidth
						name="questiontitle"
						onChange={props.onChangeHandler}
						required={true}
						error={props.errors.questiontitle}
					/>
					<Typography color="error" variant="caption">
						{props.errors.questiontitle && props.errors.questiontitle[0]}
					</Typography>
				</Grid>

				<Grid className={classes.splitFormSpacing} item xs={12}>
					{" "}
					<Grid container spacing={5}>
						<Grid item xs={6}>
							Options
						</Grid>
						<Grid item xs={6}>
							<Button variant="contained" onClick={props.addParam}>
								ADD
							</Button>
						</Grid>
					</Grid>
					{props.options.map((option, index) => {
						return (
							<Grid container spacing={2}>
								<Grid item key={"option_" + index} xs={4}>
									<TextField
										label="option"
										margin="normal"
										variant="outlined"
										style={{ width: "100%" }}
										name="option"
										onChange={e => props.handleChangeOption(e, index)}
										required="true"
									/>
									<Typography color="secondary" variant="caption">
										{props.errors.options &&
											props.errors.options[index] &&
											props.errors.options[index]}
									</Typography>
								</Grid>
								<Grid item key={"option_" + index} xs={4}>
									<Checkbox
										inputProps={{
											"aria-label": "primary checkbox"
										}}
									/>
								</Grid>
								<Grid item md={4}>
									<IconButton onClick={e => props.removeOption(e, index)}>
										<DeleteIcon />
									</IconButton>
								</Grid>
							</Grid>
						);
					})}
					<Typography color="error" variant="caption">
						{props.errors.description && props.errors.description[0]}
					</Typography>
				</Grid>
				<Grid className={classes.splitFormSpacing} item xs={12}>
					<TextField
						value={props.data.url}
						label="Content Url like https://www.example.com/article1"
						variant="outlined"
						fullWidth
						name="url"
						onChange={props.onChangeHandler}
						required={true}
						error={props.errors.url}
					/>
					<Typography color="error" variant="caption">
						{props.errors.url && props.errors.url[0]}
					</Typography>
				</Grid>
				<Grid className={classes.splitFormSpacing} item xs={12}>
					<TextField
						label=""
						variant="outlined"
						fullWidth
						name="file"
						onChange={props.onChangeHandler}
						required={true}
						type="file"
						error={props.errors.file}
					/>
					<Typography color="error" variant="caption">
						{props.errors.file && props.errors.file[0]}
					</Typography>
				</Grid>

				<Grid className={classes.splitFormSpacing} item xs={12}>
					<Grid container spacing={2}>
						<Grid item xs={6}>
							<FormControl variant="outlined" fullWidth>
								<InputLabel>Content Type</InputLabel>
								<Select
									value={props.data.type}
									name="type"
									onChange={props.onChangeHandler}
									error={props.errors.type}
									input={<OutlinedInput name="type" labelWidth={120} />}>
									{props.types.map(type => {
										return (
											<MenuItem key={type} value={type}>
												{type}
											</MenuItem>
										);
									})}
								</Select>
							</FormControl>
							<Typography color="error" variant="caption">
								{props.errors.type && props.errors.type[0]}
							</Typography>
						</Grid>
						<Grid item xs={6}>
							<FormControl variant="outlined" fullWidth>
								<InputLabel>Whom To Send</InputLabel>
								<Select
									value={props.data.year}
									name="year"
									error={props.errors.year}
									onChange={props.onChangeHandler}
									input={<OutlinedInput name="year" labelWidth={120} />}>
									{props.years.map(year => {
										return (
											<MenuItem key={year} value={year}>
												{year}
											</MenuItem>
										);
									})}
								</Select>
							</FormControl>
							<Typography color="error" variant="caption">
								{props.errors.year && props.errors.year[0]}
							</Typography>
						</Grid>
					</Grid>
				</Grid>
			</DialogContent>
			<DialogActions>
				<Button variant="contained" color="primary" onClick={props.onSubmit}>
					Send Content
				</Button>
			</DialogActions>
		</Dialog>
	);
};

export default SendContent;
