import React, { Component } from "react";

// Externals
import classNames from "classnames";
import moment from "moment";
import PerfectScrollbar from "react-perfect-scrollbar";
import PropTypes from "prop-types";

// Material helpers
import { withStyles } from "@material-ui/styles";

// Material components
import {
	Button,
	CircularProgress,
	Table,
	TableBody,
	TableCell,
	TableHead,
	TableRow,
	Tooltip,
	TableSortLabel
} from "@material-ui/core";

// Shared services
import { getOrders } from "services/order";

// Shared components
import {
	Portlet,
	PortletHeader,
	PortletLabel,
	PortletToolbar,
	PortletContent,
	Status
} from "components";

// Component styles
import styles from "./styles";
// Palette
import palette from "theme/palette";

const statusColors = {
	delivered: "success",
	pending: "info",
	refund: "danger"
};

class SkillComposition extends Component {
	constructor(props) {
		super(props);
		this.state = {};
	}
	// Chart options
	options = {
		legend: {
			display: false
		},
		responsive: true,
		maintainAspectRatio: false,
		animation: false,
		cutoutPercentage: 80,
		layout: { padding: 0 },
		tooltips: {
			enabled: true,
			mode: "index",
			intersect: false,
			borderWidth: 1,
			borderColor: palette.border,
			backgroundColor: palette.common.white,
			titleFontColor: palette.text.primary,
			bodyFontColor: palette.text.secondary,
			footerFontColor: palette.text.secondary
		}
	};
	render() {
		let data = {
			datasets: [
				{
					data: this.props.data,
					backgroundColor: [
						palette.primary.main,
						palette.danger.main,
						palette.warning.main
					],
					borderWidth: 8,
					borderColor: palette.common.white,
					hoverBorderColor: palette.common.white
				}
			],
			labels: this.props.labels
		};

		const { classes, className, ...rest } = this.props;

		const rootClassName = classNames(classes.root, className);

		return (
			<Portlet {...rest} className={rootClassName}>
				<PortletHeader noDivider>
					<PortletLabel title="Content Categorization By SkillSet" />
				</PortletHeader>
				<PortletContent>
					<div className={classes.chartWrapper}>
						<Doughnut data={data} options={this.options} />
					</div>
					<div className={classes.stats}>
						{this.props.labels.map((label, index) => {
							return (
								<div className={classes.device}>
									<Typography variant="body1">{label}</Typography>
									<Typography
										style={{ color: palette.primary.main }}
										variant="h2">
										{this.props.data[index]}
									</Typography>
								</div>
							);
						})}
					</div>
				</PortletContent>
			</Portlet>
		);
	}
}

SkillComposition.propTypes = {
	className: PropTypes.string,
	classes: PropTypes.object.isRequired
};

export default withStyles(styles)(SkillComposition);
