import React from "react";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";
import TextField from "@material-ui/core/TextField";
import { Divider } from "@material-ui/core";
import Button from "@material-ui/core/Button";
import Select from "@material-ui/core/Select";
import MenuItem from "@material-ui/core/MenuItem";

import DeleteIcon from "@material-ui/icons/Delete";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import Dialog from "@material-ui/core/Dialog";
import DialogTitle from "@material-ui/core/DialogTitle";
import IconButton from "@material-ui/core/IconButton";
import CloseIcon from "@material-ui/icons/Close";
import { makeStyles } from "@material-ui/styles";
import InputLabel from "@material-ui/core/InputLabel";
import FormControl from "@material-ui/core/FormControl";
import OutlinedInput from "@material-ui/core/OutlinedInput";

const useStyles = makeStyles(theme => ({
	closeButton: {
		position: "absolute",
		right: theme.spacing(1),
		top: theme.spacing(1),
		color: theme.palette.grey[500]
	},
	splitFormSpacing: {
		marginTop: "16px",
		marginBottom: "8px"
	},
	chips: {
		display: "flex",
		flexWrap: "wrap"
	},
	chip: {
		margin: 0.5
	}
}));

const CreateEvent = props => {
	const classes = useStyles();
	return (
		<Dialog
			onClose={props.handleClose}
			open={props.open}
			fullWidth
			maxWidth="md"
			disableBackdropClick={true}>
			<DialogTitle>
				<Typography variant="h3">Create Event</Typography>

				<IconButton className={classes.closeButton} onClick={props.handleClose}>
					<CloseIcon />
				</IconButton>
			</DialogTitle>
			<DialogContent dividers>
				<Grid container style={{ paddingLeft: "17px", paddingRight: "17px" }}>
					<Grid item xs={12}>
						<TextField
							label="Event Name"
							margin="normal"
							variant="outlined"
							style={{ width: "100%" }}
							name="eventname"
							onChange={props.onChangeHandler}
							required="true"
						/>
						<Typography color="error" variant="caption">
							{props.errors.eventname && props.errors.eventname[0]}
						</Typography>
					</Grid>

					<Grid item xs={12}>
						<Grid container spacing={4}>
							<Grid item md={6}>
								<TextField
									label="Start Date"
									type="datetime-local"
									defaultValue="2017-05-24T10:30"
									margin="normal"
									style={{ width: "100%" }}
									name="startdate"
									onChange={props.onChangeHandler}
									required="true"
								/>
								<Typography color="error" variant="caption">
									{props.errors.startdate && props.errors.startdate[0]}
								</Typography>
							</Grid>
							<Grid item md={6}>
								<TextField
									label="End Date"
									type="datetime-local"
									defaultValue="2017-05-24T10:30"
									margin="normal"
									style={{ width: "100%" }}
									name="enddate"
									onChange={props.onChangeHandler}
									required="true"
								/>
								<Typography color="error" variant="caption">
									{props.errors.enddate && props.errors.enddate[0]}
								</Typography>
							</Grid>
						</Grid>
					</Grid>

					<Grid className={classes.splitFormSpacing} item xs={12}>
						<FormControl variant="outlined" fullWidth>
							<InputLabel>Choose a Faculty Group</InputLabel>
							<Select
								value={props.facultygroup}
								name="facultygroup"
								style={{ width: "100%" }}
								onChange={props.onChangeHandler}
								input={<OutlinedInput name="facultygroup" labelWidth={140} />}>
								{props.facultygroupslist.map(value => {
									return (
										<MenuItem value={value} key={value.id}>
											{value.name}
										</MenuItem>
									);
								})}
							</Select>
						</FormControl>
						<Typography color="error" variant="caption">
							{props.errors.facultygroup && props.errors.facultygroup[0]}
						</Typography>
					</Grid>
					<Grid className={classes.splitFormSpacing} item xs={12}>
						<FormControl variant="outlined" fullWidth>
							<InputLabel>Choose a Faculty Group</InputLabel>
							<Select
								value={props.projectgroup}
								name="projectgroup"
								style={{ width: "100%" }}
								onChange={props.onChangeHandler}
								input={<OutlinedInput name="projectgroup" labelWidth={140} />}>
								{props.projectgroupslist.map(value => {
									return (
										<MenuItem value={value} key={value.id}>
											{value.name}
										</MenuItem>
									);
								})}
							</Select>
						</FormControl>
						<Typography color="error" variant="caption">
							{props.errors.projectgroup && props.errors.projectgroup[0]}
						</Typography>
					</Grid>

					<Grid item xs={12}>
						<Grid container spacing={5}>
							<Grid item xs={6}>
								Parameter
							</Grid>
							<Grid item xs={6}>
								<Button variant="contained" onClick={props.addParam}>
									ADD
								</Button>
							</Grid>
						</Grid>

						{props.parameters.map((param, index) => {
							return (
								<Grid container spacing={2}>
									<Grid item key={"param_" + index} xs={4}>
										<TextField
											label="Name"
											margin="normal"
											variant="outlined"
											style={{ width: "100%" }}
											name="Name"
											onChange={e => props.handleChangeParam(e, index)}
											required="true"
										/>
										<Typography color="error" variant="caption">
											{props.errors.parameters &&
												props.errors.parameters[index] &&
												props.errors.parameters[index].Name}
										</Typography>
									</Grid>
									<Grid item key={"param_" + index} xs={4}>
										<TextField
											label="Maximum Marks"
											type="number"
											margin="normal"
											variant="outlined"
											style={{ width: "100%" }}
											name="MaximumMarks"
											onChange={e => props.handleChangeParam(e, index)}
											required="true"
										/>
										<Typography color="error" variant="caption">
											{props.errors.parameters &&
												props.errors.parameters[index] &&
												props.errors.parameters[index].MaximumMarks}
										</Typography>
									</Grid>
									<Grid item md={4}>
										<IconButton onClick={e => props.removeParam(e, index)}>
											<DeleteIcon />
										</IconButton>
									</Grid>
								</Grid>
							);
						})}
					</Grid>
				</Grid>
			</DialogContent>
			<DialogActions>
				<Button variant="contained" color="primary" onClick={props.onSubmit}>
					Create Event
				</Button>
			</DialogActions>
		</Dialog>
	);
};

export default CreateEvent;
