class Serializer {
	constructor(student) {
		this.id = student.id;
		this.name = student.firstName + " " + student.lastName;
		this.rollNumber = student.student.id;
		this.department = student.student.classRoom.department.name;
		this.year = 1;
		this.section = student.student.classRoom.department.section;
	}
}

export default student => ({
	id: student.id,
	name: student.firstName + " " + student.lastName,
	rollNumber: student.student.id,
	department: student.student.classRoom.department.name,
	year: 1,
	section: student.student.classRoom.department.section
});
