export class Quiz {
	constructor(quiz) {
		this.id = quiz.id;
		this.title = quiz.title;
		this.skills = quiz.id;
		this.numberOfQuestions = Number(quiz.noOfQuestions);
		this.schedulingStatus = this.getSchedulindStatus(quiz);

		this.sechedule = quiz.id;
	}

	getSchedulindStatus(quiz) {
		let a = 0;
		if (quiz.isActive && quiz.schedule) {
			a = 1;
			return a;
		} else if (!quiz.isActive && quiz.schedule) {
			a = 2;
			return a;
		} else if (!quiz.expiry) {
			a = 0;
			return a;
		} else if (
			quiz.isActive &&
			quiz.schedule &&
			validate(quiz.expiry, {
				expiry: {
					datetime: {
						dateOnly: true,
						latest: moment.utc().subtract(18, "years"),
						message: "^You need to be at least 18 years old"
					}
				}
			})
		) {
			a = 3;
			return a;
		}
	}
}
