import React, { Component } from "react";
import ContentForm from "./Form";
import validate from "validate.js";
import { sendContent, loadStudentGroups, loadLectures } from "api/contents";
import { StudentGroup, Lecture } from "serializers/content";
import { connect } from "react-redux";

class SendContent extends Component {
	constructor(props) {
		super(props);

		this.state = this.getInitialState(props);
	}

	types = ["Article", "Video", "Research Paper", "Assignment"];

	static defaultProps = {
		mode: "Send"
	};

	getInitialState = props => {
		if (props.mode === "Edit") {
			return {
				data: {
					title: "",
					description: "",
					url: "",
					type: "",
					receipents: [],
					file: null,
					...props.data
				},
				receipents: [],
				errors: {}
			};
		} else {
			// default mode is assumed to be Send
			return {
				data: {
					title: "",
					description: "",
					url: "",
					type: "",
					receipents: [],
					file: null
				},
				receipents: [],
				errors: {}
			};
		}
	};

	componentDidMount() {
		this.loadreceipents();
	}

	loadreceipents = () => {
		loadStudentGroups(this.props.token)
			.then(data => {
				let studentgroups = data.groups.map(group => {
					return new StudentGroup(group);
				});

				this.setState(prevState => {
					return {
						receipents: [...prevState.receipents, ...studentgroups]
					};
				});
			})
			.catch();

		loadLectures(this.props.token)
			.then(data => {
				console.log(data.lectures);
				let lectures = data.lectures.map(lecture => {
					return new Lecture(lecture);
				});

				this.setState(prevState => {
					return {
						receipents: [...prevState.receipents, ...lectures]
					};
				});
			})
			.catch();
	};

	onChangeHandler = e => {
		const { name, value } = e.target;

		this.setState(prevState => {
			return {
				data: {
					...prevState.data,
					[name]: value
				},
				errors: {
					...prevState.errors,
					[name]: null
				}
			};
		});
	};
	onfileChangeHandler = event => {
		let file = event.target.files[0];
		console.log(file);
		this.setState(prevState => {
			return {
				data: { ...prevState.data, file: file },
				errors: {
					...prevState.errors,
					file: null,
					filesizeError: null
				}
			};
		});
	};

	onSelectChangeHandler = e => {
		let receipents = [...e.target.value];

		console.log(receipents);
		this.setState(prevState => {
			return {
				data: {
					...prevState.data,
					receipents: receipents
				},
				errors: {
					...prevState.errors,
					receipents: null
				}
			};
		});
	};

	onSubmitHandler = () => {
		console.log("submission handler");
		//validation
		let errors = validate(this.state.data, {
			title: { presence: { allowEmpty: false, message: "is required" } },
			description: { presence: { allowEmpty: false, message: "is required" } },
			url: { url: true },
			type: { presence: { allowEmpty: false, message: "is required" } },
			receipents: { presence: { allowEmpty: false, message: "are required" } },
			file: {
				presence: { allowEmpty: false, message: "is required" }
			}
		});
		let filesizeErr = "";
		if (this.state.data.file) {
			filesizeErr =
				this.state.data.file.size > 2 * 1024 * 1024
					? "File is too large"
					: null;
		}
		if (filesizeErr && errors) {
			this.setState(prevState => {
				return {
					data: {
						...prevState.data
					},

					errors: {
						...prevState.errors,
						...errors,
						filesizeError: filesizeErr
					}
				};
			});
		} else if (errors) {
			this.setState({ errors });
		} else if (filesizeErr) {
			this.setState(prevState => {
				return {
					data: {
						...prevState.data
					},

					errors: {
						...prevState.errors,

						filesizeError: filesizeErr
					}
				};
			});
		} else {
			this.sendContent();
		}
	};

	sendContent = () => {
		var form = new FormData();
		form.append("sendTo", this.state.data.receipents);
		form.append("title", this.state.data.title);
		form.append("description", this.state.data.description);
		form.append("type", this.state.data.type);
		form.append("url", this.state.data.url);
		form.append("file", this.state.data.file);

		// sendContent(this.props.token , form)
		// 	.then(() => {})
		// 	.catch(error => {
		// 		console.log(error);
		// 	});
	};

	render() {
		return (
			<ContentForm
				open={this.props.open}
				onChangeHandler={this.onChangeHandler}
				onfileChangeHandler={this.onfileChangeHandler}
				onSubmit={this.onSubmitHandler}
				errors={this.state.errors}
				data={this.state.data}
				receipents={this.state.receipents}
				types={this.types}
				handleClose={this.props.handleClose}
				onSelectChangeHandler={this.onSelectChangeHandler}
			/>
		);
	}
}

const mapStateToProps = state => {
	return {
		token: state.auth.token
	};
};

export default connect(mapStateToProps)(SendContent);
