import React, { Component } from "react";

import { Dashboard as DashboardLayout } from "layouts";

import CreateGroupToolbar from "./components/CreateGroupToolbar";
import CreateGroup from "./components/CreateGroup";
import MaterialTable from "material-table";
import { CREATEGROUP } from "../../constants";
import { fetchStudents } from "api/students";
import { connect } from "react-redux";

// styles
import styles from "./styles";
import { withStyles } from "@material-ui/styles";
import swal from "sweetalert";

class Creategroup extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: { selectedStudents: [] },
			studentTableData: null,
			form: { open: false }
		};
	}

	componentDidMount() {
		this.loadData();
	}

	loadData = () => {
		fetchStudents(this.props.token).then(({ students }) => {
			console.log(students);
			this.setState({
				studentTableData: students
			});
		});
	};

	columns = [
		{
			field: "name",
			title: "Name"
		},
		{
			field: "rollNumber",
			title: "Roll Number",
			type: "numeric"
		},
		{
			field: "department",
			title: "Department"
		},
		{
			field: "year",
			title: "Year",
			type: "numeric"
		},
		{
			field: "section",
			title: "Section"
		},
		{
			field: "Id",
			title: "Id",
			hidden: true
		}
	];

	data = [
		{
			id: "dsfsd",
			name: "Joe James",
			rollNumber: 1,
			department: "CSE",
			year: 1,
			section: "A"
		},
		{
			id: "dsfsd",
			name: "Joe James",
			rollNumber: 1,
			department: "CSE",
			year: 1,
			section: "A"
		},
		{
			id: "dsfsd",
			name: "Joe James",
			rollNumber: 1,
			department: "CSE",
			year: 1,
			section: "A"
		},
		{
			id: "dsfsd",
			name: "Joe James",
			rollNumber: 1,
			department: "CSE",
			year: 1,
			section: "A"
		}
	];

	handleSelection = rows => {
		console.log(rows);

		let selectedStudents = rows.map(row => {
			return row.Id;
		});
		this.setState(prevState => {
			return {
				data: {
					...prevState.data,
					selectedStudents: [...selectedStudents]
				}
			};
		});
	};
	handleOpenForm = () => {
		this.state.data.selectedStudents.length >= 2
			? this.setState(prevState => {
					return {
						form: {
							...prevState.form,
							open: true
						}
					};
			  })
			: swal({
					title: "Select Atleast Two Students",

					icon: "error",
					button: "Ok"
			  });
	};

	handleClose = () => {
		this.setState(prevState => {
			return {
				form: {
					...prevState.form,
					open: false
				}
			};
		});
	};

	render() {
		const { classes } = this.props;

		return (
			<DashboardLayout title="Create Group" mode={CREATEGROUP}>
				<div className={classes.root}>
					<CreateGroupToolbar handleOpenForm={this.handleOpenForm} />
					<div className={classes.content}>
						{this.state.form.open && (
							<CreateGroup
								open={this.state.form.open}
								handleClose={this.handleClose}
								numberOfStudents={this.state.data.selectedStudents.length}
							/>
						)}
						<MaterialTable
							title={"Student  List"}
							data={this.data}
							columns={this.columns}
							onSelectionChange={this.handleSelection}
							options={{
								filtering: true,
								selection: true
							}}
						/>
					</div>
				</div>
			</DashboardLayout>
		);
	}
}

const mapStateToProps = state => {
	return {
		token: state.token
	};
};

export default connect(mapStateToProps)(withStyles(styles)(Creategroup));
