import validate from "validate.js";

import moment from "moment";

validate.extend(validate.validators.datetime, {
	// The value is guaranteed not to be null or undefined but otherwise it
	// could be anything.
	parse: function(value, options) {
		return +moment.utc(value);
	},
	// Input is a unix timestamp
	format: function(value, options) {
		var format = options.dateOnly ? "YYYY-MM-DD" : "YYYY-MM-DD hh:mm:ss";
		return moment.utc(value).format(format);
	}
});

export class Quiz {
	constructor(quiz) {
		this.id = quiz.id;
		this.title = quiz.title;
		this.skills = quiz.tags;
		this.numberOfQuestions = Number(quiz.noOfQuestions);
		this.schedulingStatus = this.getSchedulingStatus(quiz);

		this.schedule = quiz.sechedule;
	}

	getSchedulingStatus(quiz) {
		let a = 0;
		if (quiz.isActive && quiz.schedule) {
			a = 1;
			return a;
		} else if (!quiz.isActive && quiz.schedule) {
			a = 2;
			return a;
		} else if (!quiz.expiry) {
			a = 0;
			return a;
		} else if (
			quiz.isActive &&
			quiz.schedule &&
			validate(quiz.expiry, {
				expiry: {
					datetime: {
						earliest: moment.utc(),
						message: "Expired"
					}
				}
			})
		) {
			a = 3;
			return a;
		}
	}
}
