import React, { Component } from "react";

import { Dashboard as DashboardLayout } from "layouts";

import StudymaterialToolbar from "./components/StudymaterialToolbar";
import SendContent from "./components/SendContent";
import Loader from "./components/loader";
import MaterialTable from "material-table";
import { fetchContents } from "api/contents";
import { connect } from "react-redux";
import { Content } from "serializers/content";

// styles
import styles from "./styles";
import { withStyles } from "@material-ui/styles";

class StudymaterialList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			open: false,
			mode: "Send",
			editData: null,
			contentTableData: [],
			isLoading: true
		};
	}

	componentDidMount() {
		this.loadData();
	}
	loadData = () => {
		fetchContents(this.props.token)
			.then(response => {
				console.log(response.tasks);
				let contentTableData = response.tasks.map(task => {
					return new Content(task);
				});

				this.setState({
					contentTableData: contentTableData,
					isLoading: false
				});
			})
			.catch(error => {
				console.log(error);
			});
	};

	handleClickOpen = (mode = "Send", editData = null) => {
		this.setState({
			open: true,
			mode,
			editData
		});
	};
	handleClose = () => {
		this.setState({
			open: false,
			mode: "Send",
			editData: null
		});
	};

	updateContentTableData = newrow => {
		let contentTableData = [...this.state.contentTableData];
		contentTableData.push(new Content(newrow));
		this.setState({
			contentTableData
		});
	};
	columns = [
		{ title: "Title", field: "title" },
		{ title: "Description", field: "description" },
		{ title: "Type", field: "type" },
		{
			title: "Groups",
			field: "groups",
			render: rowData =>
				rowData.groups.map(group => {
					return <li>{group}</li>;
				})
		},
		{
			title: "Lectures",
			field: "lectures",
			render: rowData =>
				rowData.lectures.map(lecture => {
					return <li>{lecture}</li>;
				})
		},
		{
			title: "Url",
			field: "url",
			hidden: true
		},
		{
			title: "FileUrl",
			field: "fileurl",
			hidden: true
		},
		{
			title: "id",
			field: "id",
			hidden: true
		}
	];

	actions = [
		{
			icon: "visibility",
			tooltip: "View Content url",
			onClick: (event, rowData) => {
				var win = window.open(rowData.url, "_blank");
				win.focus();
			}
		},
		{
			icon: "visibility",
			tooltip: "View Content file",
			onClick: (event, rowData) => {
				var win = window.open(rowData.fileurl, "_blank");
				win.focus();
			}
		},
		{
			icon: "edit",
			tooltip: "Edit Content",
			onClick: (event, rowData) => {
				console.log(rowData);
				return this.handleClickOpen("Edit", rowData);
			}
		}
	];

	render() {
		const { classes } = this.props;

		return (
			<DashboardLayout title="Studymaterial">
				<div className={classes.root}>
					<StudymaterialToolbar handleClickOpen={this.handleClickOpen} />
					{this.state.open && (
						<SendContent
							open={this.state.open}
							handleClose={this.handleClose}
							mode={this.state.mode}
							data={this.state.editData}
							updateContentTableData={this.updateContentTableData}
						/>
					)}
					<div className={classes.content}>
						{!this.state.isLoading ? (
							<MaterialTable
								columns={this.columns}
								data={this.state.contentTableData}
								options={{
									filtering: true,
									actionsColumnIndex: -1
								}}
								actions={this.actions}
								title="Study Material List"
							/>
						) : (
							<Loader />
						)}
					</div>
				</div>
			</DashboardLayout>
		);
	}
}

const mapStateToProps = state => {
	return {
		token: state.auth.token
	};
};

export default connect(mapStateToProps)(withStyles(styles)(StudymaterialList));
