import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Stepper from "@material-ui/core/Stepper";
import Step from "@material-ui/core/Step";
import StepLabel from "@material-ui/core/StepLabel";
import StepContent from "@material-ui/core/StepContent";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import { withStyles } from "@material-ui/styles";
import Styles from "./styles";

class Milestones extends Component {
	constructor(props) {
		super(props);
		this.state = {
			activeStep: 0,
			milestones: []
		};
	}

	handleNext = () => {
		this.setState(prevState => {
			return {
				activeStep: prevState.activeStep + 1
			};
		});
	};

	handleBack = () => {
		setActiveStep(prevActiveStep => prevActiveStep - 1);
	};

	handleReset = () => {
		setActiveStep(0);
	};

	render() {
		return (
			<div className={this.props.classes.root}>
				<div className={this.props.classes.heading}>
					<Typography variant="h4">Milestones</Typography>
				</div>
				<Stepper activeStep={this.state.activeStep} orientation="vertical">
					{this.state.milestones.map((milestone, index) => (
						<Step key={milestone.id}>
							<StepLabel>{milestone.title}</StepLabel>
							<StepContent>
								<Typography>{milestone.description}</Typography>
								<div className={this.props.classes.actionsContainer}>
									<div>
										<Button
											onClick={this.handleBack}
											className={this.props.classes.button}>
											Reject
										</Button>
										<Button
											variant="contained"
											color="primary"
											onClick={this.handleNext}
											className={this.props.classes.button}>
											Approve
										</Button>
									</div>
								</div>
							</StepContent>
						</Step>
					))}
				</Stepper>
				{this.state.activeStep === this.state.milestones.length && (
					<Paper
						square
						elevation={0}
						className={this.props.classes.resetContainer}>
						<Typography>
							All milestones completed - you&apos;re finished
						</Typography>
						<Button
							onClick={this.handleReset}
							className={this.props.classes.button}>
							Reset
						</Button>
					</Paper>
				)}
			</div>
		);
	}
}

export default withStyles(Styles)(Milestones);
