import React, { Component } from "react";

import { Dashboard as DashboardLayout } from "layouts";

import ProjectToolbar from "./components/ProjectToolbar";
import MaterialTable from "material-table";
import IconButton from "@material-ui/core/IconButton";
import ViewIcon from "@material-ui/icons/Visibility";
import { Loader } from "components";
import { connect } from "react-redux";
// styles
import styles from "./styles";
import { withStyles } from "@material-ui/styles";
import { loadProjectList } from "api/projects";
import { Project } from "serializers/projects";
import CreateEvent from "./components/CreateEvent";
import Milestone from "./components/Milestone";
import Projectgroup from "./components/Projectgroup";
import swal from "sweetalert";
import { Typography } from "@material-ui/core";
class ProjectList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {},
			isLoading: true,
			open: null,
			selectedRows: []
		};
	}

	componentDidMount() {
		this.loadData();
	}

	loadData = () => {
		loadProjectList(this.props.token)
			.then(data => {
				let projects = data.projects.map(project => {
					return new Project(project);
				});
				// push data to redux
				this.props.pushProjectListdata(projects);
				this.setState(prevState => {
					return {
						isLoading: false
					};
				});
			})
			.catch(error => {
				swal({
					title: "Internal  Server Error",
					text: "Please Try Refreshing Page",

					button: "Ok"
				});
			});
	};

	columns = [
		{
			field: "id",
			title: "id",
			hidden: true,
			options: {
				filter: true,
				sort: true
			}
		},
		{
			field: "title",
			title: "Title",
			options: {
				filter: true,
				sort: true
			},
			cellStyle: {
				"font-size": "15px"
			}
		},
		{
			field: "skills",
			title: "Skills",
			options: {
				filter: true,
				sort: false
			},
			render: rowData =>
				rowData.skills.map(skill => {
					return <li>{skill.skill}</li>;
				}),
			cellStyle: {
				"font-size": "15px"
			}
		},
		{
			field: "team",
			title: "Team",
			options: {
				filter: true,
				sort: false
			},
			cellStyle: {
				"font-size": "15px"
			},
			render: rowData =>
				rowData.team.map(student => {
					return <li>{student.name}</li>;
				})
		},
		{
			field: "milestones",
			title: "milestones",
			hidden: true
		},
		{
			title: "View",
			cellStyle: {
				"font-size": "15px"
			},
			render: rowData => (
				<IconButton onClick={() => this.handleViewProject(rowData.id)}>
					<ViewIcon />
				</IconButton>
			)
		}
	];

	handleViewProject = id => {
		console.log(id);
		this.props.history.push("/project/" + id);
	};

	data = [
		{ id: "dsfsd", title: "Joe James", skills: "Test Corp", team: "Yonkers" },
		{
			id: "desd",
			title: "John Walsh",
			skills: "Test Corp",
			team: "Hartford"
		},
		{ id: "wfsd", title: "Bob Herm", skills: "Test Corp", team: "Tampa" },
		{
			id: "dsxcsfsd",
			title: "James Houston",
			skills: "Test Corp",
			team: "Dallas"
		}
	];

	handleSelection = rows => {
		this.setState({
			selectedRows: rows
		});
	};

	handleClickOpen = formName => {
		switch (formName) {
			case "Event":
				this.setState({
					open: formName
				});
				break;
			case "Group":
				this.state.selectedRows.length < 2
					? swal({
							title: "Error",
							text: "Select Atleast Two Projects",

							icon: "error",
							button: "Ok"
					  })
					: this.setState({
							open: formName
					  });
				break;

			case "Milestone":
				this.state.selectedRows.length < 1
					? swal({
							title: "Error",
							text: "Select Atleast one Project",

							icon: "error",
							button: "Ok"
					  })
					: this.setState({
							open: formName
					  });
		}
	};
	handleClose = () => {
		this.setState({
			open: null
		});
	};

	render() {
		const { classes } = this.props;

		return (
			<DashboardLayout title="Projects">
				<div className={classes.root}>
					<ProjectToolbar handleClickOpen={this.handleClickOpen} />
					{this.state.open === "Event" && (
						<CreateEvent
							open={this.state.open === "Event"}
							handleClose={this.handleClose}
						/>
					)}
					{this.state.open === "Group" && (
						<Projectgroup
							open={this.state.open === "Group"}
							handleClose={this.handleClose}
							selectedRows={this.state.selectedRows}
						/>
					)}
					{this.state.open === "Milestone" && (
						<Milestone
							open={this.state.open === "Milestone"}
							handleClose={this.handleClose}
							selectedRows={this.state.selectedRows}
						/>
					)}

					<div className={classes.content}>
						{!this.state.isLoading ? (
							<MaterialTable
								title={
									<Typography
										style={{ "font-size": "18px", fontWeight: "bolder" }}>
										Project List
									</Typography>
								}
								data={this.props.projectlistTabledata}
								columns={this.columns}
								onSelectionChange={this.handleSelection}
								options={{
									filtering: true,
									selection: true,

									headerStyle: {
										"font-size": "18px"
									}
								}}
							/>
						) : (
							<Loader />
						)}
					</div>
				</div>
			</DashboardLayout>
		);
	}
}

const mapStatetoProps = state => {
	return {
		token: state.auth.token,
		projectlistTabledata: state.project.projectlistTabledata
	};
};

const mapDispatchToProps = dispatch => {
	return {
		pushProjectListdata: data =>
			dispatch({ type: "PUSH PROJECTLIST DATA", data: data })
	};
};

export default connect(
	mapStatetoProps,
	mapDispatchToProps
)(withStyles(styles)(ProjectList));
