const initialState = {
	projectlistTabledata: []
};

const reducer = (state = initialState, action = {}) => {
	switch (action.type) {
		case "PUSH PROJECTLIST DATA":
			console.log("hey am redux");
			console.log(action.data);
			return {
				projectlistTabledata: action.data
			};
			break;
		case "UPDATE REVIEW":
			console.log("hey am update review redux");
			console.log(action.projectId);
			console.log(action.milestoneId);
			console.log(action.submissionId);
			console.log(action.updatedReview);

			let projectlistTabledata = [...state.projectlistTabledata];
			projectlistTabledata = projectlistTabledata.map(project => {
				if (project.id === action.projectId) {
					project.milestones.map(milestone => {
						if (milestone.id === action.milestoneId) {
							milestone.submissions.map(submission => {
								if (submission.id === action.submissionId) {
									(submission.status = action.updatedReview.status),
										(submission.remark = action.updatedReview.remark);
								}
							});
						}
					});
				}
				return project;
			});
			return {
				projectlistTabledata: projectlistTabledata
			};
			break;
		default:
			return state;
	}
};

export default reducer;
