import React, { Component } from "react";

import { Dashboard as DashboardLayout } from "layouts";
import { APTITUDE } from "../../constants";
import style from "./style.module.css";
// styles
import styles from "./styles";
import { withStyles } from "@material-ui/styles";
import { Typography } from "@material-ui/core";
import Radio from "@material-ui/core/Radio";
import RadioGroup from "@material-ui/core/RadioGroup";
import FormControlLabel from "@material-ui/core/FormControlLabel";

class CoreSubjects extends Component {
  constructor(props) {
    super(props);

    this.state = {
      questions: [
        {
          id: "1",
          question: "what is pointer",
          options: ["refernce", "object"],
          correct: "refernce",
          isCorrect: "NA"
        },
        {
          id: "2",
          question: "what is pointer",
          options: ["refernce", "object"],
          correct: "refernce",
          isCorrect: "NA"
        }
      ]
    };
  }

  handleChange = (e, id) => {
    // console.log(e.target.value);
    // console.log(id);
    let [...questions] = this.state.questions;
    //console.log(questions);
    questions = questions.map(ques => {
      if (ques.id == id) {
        // update isCorrect
        ques.isCorrect = e.target.value == ques.correct ? "Right" : "Wrong";
      }
      return ques;
    });

    this.setState(questions);
  };

  render() {
    const { classes } = this.props;
    return (
      <DashboardLayout mode={APTITUDE} title="Core Subjects">
        <div className={classes.root}>
          {this.state.questions.map(ques => {
            let idx = ques.id;
            return (
              <div>
                <Typography>{ques.question}</Typography>

                <RadioGroup onChange={e => this.handleChange(e, ques.id)}>
                  {ques.options.map(option => {
                    return (
                      <FormControlLabel
                        value={option}
                        control={<Radio />}
                        label={option}
                      />
                    );
                  })}
                </RadioGroup>
              </div>
            );
          })}
        </div>
      </DashboardLayout>
    );
  }
}

export default withStyles(styles)(CoreSubjects);
