import React, { Component } from "react";
// import { GiftedChat } from "react-web-gifted-chat";

// styles
import styles from "./styles";
import { withStyles } from "@material-ui/styles";

// components
import InputBar from "./InputBar";
import MessageContainer from "./MessageContainer";
import MessageBubble from "./MessageBubble";
import { Loader } from "components";
import { fetchMessages } from "api/projects";

import compose from "recompose/compose";
import { from } from "zen-observable";

class Chat extends Component {
	constructor(props) {
		super(props);
		this.state = {
			messages: [],
			isLoading: true
		};
	}

	componentDidMount() {
		this.loadMessages();
	}

	loadMessages = () => {
		fetchMessages(this.props.token, this.props.match.params.projectId)
			.then(data => console.log(data))
			.catch(error => console.log(error));
	};

	render() {
		const { classes } = this.props;

		return (
			<div className={classes.root}>
				{this.state.isLoading ? (
					<Loader />
				) : (
					<div>
						<MessageContainer>
							<MessageBubble message="Hello first" />
							<MessageBubble message="Hello Welcome" />
							<MessageBubble message="Hello garbage" />
							<MessageBubble message="Hello ok" />
							<MessageBubble message="Hello kya baat h" isRight />
							<MessageBubble message="Hello" />
							<MessageBubble message="Hello ok" />
							<MessageBubble message="Hello summery" isRight />
							<MessageBubble message="Hello" />
							<MessageBubble message="Hellosss" isRight />
							<MessageBubble message="Hello" />
						</MessageContainer>
						<InputBar projectid={this.props.match.params.projectId} />
					</div>
				)}
			</div>
		);
	}
}
const mapStatetoProps = state => {
	return {
		token: state.auth.token
	};
};

export default compose(
	connect(mapStatetoProps),
	withStyles(styles)
)(Chat);
