import { makeStyles } from "@material-ui/core/styles";

const Styles = makeStyles(theme => ({
	root: {
		position: "absolute",
		width: "100%",
		height: "74px",
		bottom: 0,
		padding: "2px 4px",
		display: "flex",
		alignItems: "center"
	},
	input: {
		marginLeft: 8,
		flex: 1
	},
	iconButton: {
		padding: 10
	}
}));

export default Styles;
