import React, { Component } from "react";
import styles from "./styles";
import { withStyles } from "@material-ui/styles";
import EditToolbar from "./components/EditToolbar";
import Header from "./components/header";
import { Typography } from "@material-ui/core";
import Visualization from "./components/Visualization";
import MaterialTable from "material-table";
import QuestionForm from "./components/QuestionsForm";
import { Loader } from "components";
import { connect } from "react-redux";
import { fetchquizdetails } from "api/quizzes";
import { Quizdetail } from "serializers/quiz";
class EditQuiz extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {
				title: "Python",
				skills: ["ds", "algo"],
				numberOfQuestions: 5,
				Questions: [
					{
						questiontitle: "what is python",
						options: ["a", "b"],
						correctOption: "a",
						difficultyLevel: "easy",
						skills: ["ds"],
						id: "1"
					},
					{
						questiontitle: "what is python",
						options: ["a", "b"],
						correctOption: "b",
						difficultyLevel: "hard",
						skills: ["algo"],
						id: "2"
					},
					{
						questiontitle: "what is python",
						options: ["a", "b"],
						correctOption: "a",
						difficultyLevel: "medium",
						skills: ["ds"],
						id: "3"
					}
				],
				estimatedTime: 160
			},
			form: {
				open: false,
				mode: "ADD",
				data: null
			},
			isLoading: true
		};
	}

	componentDidMount() {
		this.loadData();
	}

	loadData = () => {
		fetchquizdetails(this.props.token, this.props.match.params.quizId)
			.then(data => {
				console.log(data.quiz);
				this.setState({
					isLoading: false,
					data: new Quizdetail(data.quiz)
				});
			})
			.catch(error => {
				console.log(error);
			});
	};
	columns = [
		{ title: "Question", field: "questiontitle" },
		{
			title: "Options",
			field: "options",
			render: rowData =>
				rowData.options.map(option => {
					if (option == rowData.correctOption) {
						return <li style={{ color: "green" }}>{option}</li>;
					} else return <li style={{ color: "red" }}>{option}</li>;
				})
		},
		{
			title: "Difficulty Level",
			field: "difficultyLevel"
		},
		{
			title: "Skills",
			field: "skills",
			render: rowData =>
				rowData.skills.map(skill => {
					return <li>{skill}</li>;
				})
		},
		{
			title: "Id",
			field: "id",
			hidden: true
		},
		{
			title: "Correct Option",
			field: "correctOption",
			hidden: true
		}
	];

	actions = [
		{
			icon: "edit",
			tooltip: "Edit Question",
			onClick: (event, rowData) => {
				console.log(rowData);
				return this.handleOpenForm("EDIT", rowData);
			}
		},

		{
			icon: "delete_outline",
			tooltip: "Delete Question",
			onClick: (event, rowData) => {
				alert("Question deleted");
			}
		}
	];

	handleOpenForm = (mode = "ADD", data = null) => {
		this.setState({
			form: { open: true, mode, data }
		});
	};
	handleCloseForm = () => {
		this.setState({ form: { open: false, mode: "ADD", data: null } });
	};

	render() {
		const { classes } = this.props;
		return (
			<div className={classes.root}>
				{!this.state.isLoading ? (
					<div>
						<div className={classes.heading}>
							<Typography align="center" variant="h3">
								{this.state.data.title}
							</Typography>
						</div>
						<EditToolbar handleOpenForm={this.handleOpenForm} />
						{this.state.form.open && (
							<QuestionForm
								open={this.state.form.open}
								mode={this.state.form.mode}
								data={this.state.form.data}
								handleCloseForm={this.handleCloseForm}
								skills={this.state.data.skills}
							/>
						)}
						<div className={classes.content}>
							<Header
								numberOfQuestions={this.state.data.numberOfQuestions}
								estimatedTime={this.state.data.estimatedTime}
							/>

							<Visualization />

							<div>
								<MaterialTable
									columns={this.columns}
									data={this.state.data.Questions}
									options={{
										filtering: true,
										actionsColumnIndex: -1
									}}
									actions={this.actions}
									title="Questions List"
								/>
							</div>
						</div>
					</div>
				) : (
					<Loader />
				)}
			</div>
		);
	}
}

const mapStateToProps = state => {
	return {
		token: state.auth.token
	};
};

export default connect(mapStateToProps)(withStyles(styles)(EditQuiz));
