import React, { Component } from "react";
import Form from "./Form";
import validate from "validate.js";
import { connect } from "react-redux";
import { CreateProjectGroup } from "api/projects";
import swal from "sweetalert";

class CreateGroup extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {
				groupname: ""
			},
			errors: {},
			isLoading: false
		};
	}

	onChangeHandler = e => {
		const { name, value } = e.target;

		this.setState(prevState => {
			return {
				data: {
					...prevState.data,
					[name]: value
				},
				errors: {
					...prevState.errors,
					[name]: []
				}
			};
		});
	};

	onSubmitHandler = () => {
		//validation
		let errors = validate(this.state.data, {
			groupname: { presence: { allowEmpty: false, message: "is required" } }
		});
		// check for errors
		if (errors) {
			this.setState({ errors });
		} else {
			this.setState({
				isLoading: true
			});
			let projectList = this.props.selectedRows.map(row => {
				return row.id;
			});
			let data = {
				name: this.state.data.groupname,
				projectList
			};
			CreateProjectGroup(this.props.token, data)
				.then(response => {
					swal({
						title: "Success",
						text:
							"Project Group " +
							response.group.name +
							" has been created successfully",

						button: "Ok"
					});
					this.props.handleClose();
				})
				.catch(error => {
					swal({
						title: "Error",
						text: error.response.data.message,

						button: "Ok"
					});
					this.setState({
						isLoading: false
					});
				});
		}
	};

	render() {
		return (
			<Form
				open={this.props.open}
				handleClose={this.props.handleClose}
				onChangeHandler={this.onChangeHandler}
				onSubmit={this.onSubmitHandler}
				errors={this.state.errors}
			/>
		);
	}
}

const MapStatetoProps = state => {
	return {
		token: state.auth.token
	};
};

export default connect(MapStatetoProps)(CreateGroup);
