import React from "react";

import classNames from "classnames";

import Chip from "@material-ui/core/Chip";

import Card from "@material-ui/core/Card";

import CardContent from "@material-ui/core/CardContent";

import Typography from "@material-ui/core/Typography";

import useStyles from "./styles";

const MessageBubble = ({ isRight = false, ...props }) => {
	const classes = useStyles();
	let color = isRight ? "primary" : "";
	return (
		<div
			className={classNames(classes.root, {
				[classes.sideRight]: isRight
			})}
			style={{ backgroundColor: color }}>
			{/* <Chip color={isRight ? "primary" : ""} label={props.message} /> */}

			<Card className={classes.card} style={{ backgroundColor: color }}>
				<CardContent
					className={classes.CardContent}
					style={{ backgroundColor: color }}>
					<Typography
						style={{ marginLeft: "auto", fontSize: 12, color: "grey" }}>
						Rachel Geller
					</Typography>
					<Typography variant="body2" component="p" style={{ fontSize: 12 }}>
						hey there hey there hey there hey there hey
						fvsdvggsvsvsggsgdsfgsdfgsfg dfgsdfgsgfsdgsfdg dgsdgfdsgdfsdfgdfgsdfg
						gfsgdfgdgdgdgdgdfgfd gfgf gfgfg fgfgfg fggf
					</Typography>
					<Typography
						style={{ marginLeft: "auto", fontSize: 12, color: "grey" }}>
						10:45 pm
					</Typography>
				</CardContent>
			</Card>
		</div>
	);
};

export default MessageBubble;
