import React, { Component } from "react";

// Externals
import PropTypes from "prop-types";

// Material helpers
import { withStyles } from "@material-ui/styles";

// Material components
import { Grid } from "@material-ui/core";

// Shared layouts
import { Dashboard as DashboardLayout } from "layouts";

// Custom components
import {
	Content,
	Quizzes,
	Projects,
	Engagement,
	StudentCategorization,
	ProjectCompletionBargraph,
	ProductList,
	OrdersTable
} from "./components";

// Component styles
const styles = theme => ({
	root: {
		padding: theme.spacing(4)
	},
	item: {
		height: "100%"
	}
});

class Dashboard extends Component {
	constructor(props) {
		super(props);
		this.state = {
			isLoading: true
		};
	}

	render() {
		const { classes } = this.props;

		return (
			<DashboardLayout title="Dashboard">
				<div className={classes.root}>
					<Grid container spacing={4}>
						<Grid item lg={3} sm={6} xl={3} xs={12}>
							<Content className={classes.item} />
						</Grid>
						<Grid item lg={3} sm={6} xl={3} xs={12}>
							<Quizzes className={classes.item} />
						</Grid>
						<Grid item lg={3} sm={6} xl={3} xs={12}>
							<Projects className={classes.item} />
						</Grid>
						<Grid item lg={3} sm={6} xl={3} xs={12}>
							<Engagement className={classes.item} />
						</Grid>
						<Grid item lg={8} md={12} xl={9} xs={12}>
							<ProjectCompletionBargraph className={classes.item} />
						</Grid>
						<Grid item lg={4} md={6} xl={3} xs={12}>
							<StudentCategorization className={classes.item} />
						</Grid>

						<Grid item lg={4} md={6} xl={3} xs={12}>
							<ProductList className={classes.item} />
						</Grid>
						<Grid item lg={8} md={12} xl={9} xs={12}>
							<OrdersTable className={classes.item} />
						</Grid>
					</Grid>
				</div>
			</DashboardLayout>
		);
	}
}

Dashboard.propTypes = {
	classes: PropTypes.object.isRequired
};

export default withStyles(styles)(Dashboard);
