import React, { Component } from "react";
import { withStyles } from "@material-ui/styles";

import { Dashboard as DashboardLayout } from "layouts";

import styles from "./styles";

import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Grid from "@material-ui/core/Grid";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { Loader } from "components";
import { Lecture } from "serializers/classroom";
import swal from "sweetalert";

import { Portlet, PortletContent, PortletFooter } from "components";
import { loadLectures } from "api/classroom";

class ClassRoom extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: true,
      classrooms: [
        { Id: 1, name: "CSE 6A", cousrename: "Applied Mathematics" },
        { Id: 2, name: "CSE 7A", cousrename: "Applied Chemistry" },
        { Id: 3, name: "CSE 1C", cousrename: "Engineering Mechanics" },
        { Id: 4, name: "CSE 2B", cousrename: "H.V.P.E" }
      ]
    };
  }

  componentDidMount() {
    this.loadData();
  }

  loadData = () => {
    loadLectures(this.props.token)
      .then(data => {
        let classrooms = data.lectures.map(lecture => {
          return new Lecture(lecture);
        });

        this.setState({
          isLoading: false,
          classrooms
        });
      })
      .catch(error => {
        swal({
          title: "Interval Server Error",
          text: " Please Try Refreshing Page",

          button: "Ok"
        });
      });
  };

  render() {
    const { classes } = this.props;
    const bull = <span className={classes.bullet}>â€¢</span>;

    return (
      <DashboardLayout title="Game Of Convoy">
        <div className={classes.root}>
          {!this.state.isLoading ? (
            <Grid container spacing={2}>
              {this.state.classrooms.map(classroom => {
                return (
                  <Grid item md={6} className={classes.splitFormSpacing}>
                    <Portlet className={classes.portlet}>
                      <PortletContent>
                        <Typography
                          variant="h3"
                          className={classes.title}
                          color="textPrimary"
                          gutterBottom
                        >
                          {classroom.name}
                        </Typography>
                        <Typography
                          variant="h4"
                          className={classes.subtitle}
                          color="textPrimary"
                          gutterBottom
                        >
                          {classroom.cousrename}
                        </Typography>
                      </PortletContent>
                      <PortletFooter>
                        <Button size="small" variant="outlined">
                          <Link to={"/class-room/" + classroom.Id + "/details"}>
                            Know More
                          </Link>
                        </Button>
                      </PortletFooter>
                    </Portlet>
                  </Grid>
                );
              })}
            </Grid>
          ) : (
            <Loader />
          )}
        </div>
      </DashboardLayout>
    );
  }
}

const MapStatetoProps = state => {
  return {
    token: state.auth.token
  };
};

export default connect(MapStatetoProps)(withStyles(styles)(ClassRoom));
