import React, { Component } from "react";
import Container from "@material-ui/core/Container";
import Grid from "@material-ui/core/Grid";
import Typography from "@material-ui/core/Typography";

import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";

import moment from "moment";
import { Link } from "react-router-dom";
import styles from "./styles";
import { withStyles } from "@material-ui/styles";

class schedulequiz extends Component {
	constructor(props) {
		super(props);
		this.state = {
			data: {
				quiztitle: "Python",
				schedulingStatus: "Scheduled",
				startdate: "",
				enddate: "",
				timelimit: ""
			},
			error: null
		};
	}

	startdateHandler = e => {
		this.setState({ startdate: e.target.value });
	};

	enddateHandler = e => {
		this.setState({ enddate: e.target.value });
	};

	timelimitHandler = e => {
		this.setState({ timelimit: e.target.value });
	};

	schedulequizHandler = () => {
		console.log(this.state.timelimit);
		console.log(this.state.startdate);
		console.log(this.state.enddate);

		// validation of user input
		// if (
		// 	isEmpty(this.state.timelimit) ||
		// 	isEmpty(this.state.startdate) ||
		// 	isEmpty(this.state.enddate)
		// ) {
		// 	this.setState({ error: "PLease fill in all the fields" });
		// } else if (this.state.timelimit < 60) {
		// 	this.setState({ error: "Please increase the time limit" });
		// } else if (
		// 	moment(this.state.enddate).isSameOrBefore(this.state.startdate)
		// ) {
		// 	this.setState({ error: "Invalid Schedule" });
		// } else {
		// 	this.setState({ error: null });
		// }

		// database call
	};

	render() {
		return (
			<div className={this.props.classes.root}>
				<Typography style={{ textAlign: "center" }} variant="h3">
					{this.state.data.quiztitle}
				</Typography>
				<Typography style={{ textAlign: "center" }} variant="subtitle1">
					Scheduling Status: {this.state.data.schedulingStatus}
				</Typography>
				<Grid container spacing={2} className={this.props.classes.content}>
					<Grid item md={4}>
						<Typography>Start Date</Typography>
						<TextField
							type="date"
							defaultValue="2017-05-24"
							onChange={this.startdateHandler}
						/>
						<br />
						<Typography variant="caption">Day when quiz begins</Typography>
					</Grid>
					<Grid item md={4}>
						<Typography>End Date</Typography>
						<TextField
							type="date"
							defaultValue="2017-05-24"
							onChange={this.enddateHandler}
						/>
						<br />
						<Typography variant="caption">Last day to give quiz</Typography>
					</Grid>
					<Grid item md={4}>
						<Typography>Time limit</Typography>

						<TextField
							label="Number"
							type="number"
							onChange={this.timelimitHandler}
						/>
						<br />
						<Typography variant="caption">(in seconds)</Typography>
					</Grid>
				</Grid>

				<Typography style={{ textAlign: "center", color: "red" }}>
					{this.state.error}
				</Typography>
				<br />

				<Button
					variant="contained"
					color="primary"
					onClick={this.schedulequizHandler}>
					{this.state.data.schedulingStatus === "Scheduled"
						? "Reschedule Quiz"
						: "Schedule Quiz"}
				</Button>
			</div>
		);
	}
}

export default withStyles(styles)(schedulequiz);
